import React, { useEffect, useState } from 'react'
import '../Home.css';
import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import Slider from "react-slick";
import Lottie from 'react-lottie';

import { connect } from 'react-redux';
import { selectSocialMediaPage, getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import PageCard from './PageCard';

import animationData from '../../../assets/json/loading.json';

function Pages(props) {

    const [loadingPages, setLoadingPages] = useState(true);
    // const [facebookPages, setFacebookPages] = useState(null);
    // const [instagramPages, setInstagramPages] = useState(null);

    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    const settings = {
        // dots: true,
        infinite: false,
        speed: 700,
        slidesToShow: 3.7,
        slidesToScroll: 3,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
    };

    useEffect(() => {
        props.getFbData();
        props.getInstaData();
        setTimeout(() => {
            setLoadingPages(false);
        },2000);
    },[]);

    useEffect(() => {
        if(props.fbData) {
            console.log("Pages fb data =>", props.fbData);
        }
    },[props.fbData]);
    
    useEffect(() => {
        if(props.instaData) {
            console.log("Pages insta Data =>", props.instaData);
        }
    },[props.instaData]);

    return (
    <>
        <Row>
            {
            props.title 
            ?
                <p className="home-big-title" style={props.titleStyling ? props.titleStyling : {} }>{props.title}</p>
            :
                <p className="home-big-title">Pages Connected</p>
            }
        </Row>

        <Row>

            {
            !loadingPages
            ?
                (
                    <>

                    {props.fbData && props.instaData && props.fbData.length + props.instaData.length > 3
                    ?
                    (
                    <div style={{ width: '95%', height: '400px' }}>
                        <Slider {...settings}>
                            {
                            props.fbData !== null &&
                                props.fbData.map((page,index) => 
                                    <div key={index} onClick={() => props.selectSocialMediaPage(page,"fb",page.status)}>
                                        <PageCard 
                                            platform={"fb"}
                                            isConnectedPage={page.isConnected} 
                                            namePage={page.name} 
                                            idPage={page.idPage} 
                                            picture={null} 
                                            accessToken={page.access_token} 
                                            status={page.status} 
                                            team={page.user} 
                                            admins={page.admins} 
                                            setLoadingPages={setLoadingPages} 
                                        />
                                    </div>
                                )
                            }
                            
                            {
                            props.instaData !== null &&
                                props.instaData.map((page,index) => 
                                    <div key={index} onClick={() => props.selectSocialMediaPage(page,"insta",page.status)}>
                                        <PageCard 
                                            platform={"insta"} 
                                            isConnectedPage={page.isConnected}
                                            namePage={page.name} 
                                            idPage={page.idPage} 
                                            picture={page.imageUrl} 
                                            accessToken={page.access_token} 
                                            status={page.status} 
                                            team={page.user} 
                                            admins={page.admins} 
                                            setLoadingPages={setLoadingPages}
                                        />
                                    </div>
                                )
                            }
                        </Slider>
                    </div>
                    )
                    :
                    (
                    <Row className="d-flex">
                        {
                        props.fbData !== null &&
                            props.fbData.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"fb",page.status)}>
                                    <PageCard 
                                        platform={"fb"}
                                        isConnectedPage={page.isConnected} 
                                        namePage={page.name} 
                                        idPage={page.idPage} 
                                        picture={null} 
                                        accessToken={page.access_token} 
                                        status={page.status} 
                                        team={page.user} 
                                        admins={page.admins} 
                                        setLoadingPages={setLoadingPages} 
                                    />
                                </div>
                            )
                        }
                            
                        {
                        props.instaData !== null &&
                            props.instaData.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"insta",page.status)}>
                                    <PageCard 
                                        platform={"insta"} 
                                        isConnectedPage={page.isConnected}
                                        namePage={page.name} 
                                        idPage={page.idPage} 
                                        picture={page.imageUrl} 
                                        accessToken={page.access_token} 
                                        status={page.status} 
                                        team={page.user} 
                                        admins={page.admins} 
                                        setLoadingPages={setLoadingPages}
                                    />
                                </div>
                            )
                        }
                    </Row>
                    )

                    }
                    </>
                )
            :
                (
                <div className="d-flex" style={{ width: '100%' }}>
                    <div className="m-auto">
                        <Lottie options={defaultOptions} width={200} />
                    </div>
                </div>
                )
            }
        </Row>
    </>
    );
}

const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { selectSocialMediaPage,getFbData, getInstaData })(Pages);
