import React, { useEffect } from 'react'
import '../Home.css';

import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';

import { connect } from 'react-redux';
import { getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import OwlCarousel from 'react-owl-carousel';
const DISC_ICON = require('../../../assets/images/home/disconnected-icon.png');
const CONN_ICON = require('../../../assets/images/home/connected-icon.png');
const PROFILE_PHOTO = require('../../../assets/images/home/profile-photo.png');
const INSTA_ICON = require('../../../assets/images/home/insta-icon.png');


function Pages(props) {

    useEffect(() => {
        // Getting insta & fb DATA :
        setTimeout(() => {
            props.getFbData();
        },1500);
        
        props.getInstaData();

    },[]);

    useEffect(() => {
        fetch(`https://graph.facebook.com/17841439275893599?access_token=EAALJB1gyZAkYBAGo4Ui1O7heDHHAzer3PjMvR2CQqTRpvH3slVZArBXOWw80tBc5K28Jq5x3wSQGZA4BlzSNdcNIwlSIT0pL3TZB19qOu3Vz8OIVoVjVeBlDkanghqZCY9cm1Y2gc3PvubOOSLDO4g0rrNMZCwtI6QmEPujJSHUyOsSMyqVdc0&fields=name`)
        .then(res => res.json())
        .then(data => {
            console.log("Facebook Grappph =>",data);
        })
    }, [])


    {/* PAGE CARD */}
    const renderPageCard = () => (
        /* if clicked border: 1px solid red; */
        <div className="pages-card m-1" style={{backgroundColor: "white"}}>
            <Row className="d-flex">
                <Image src={DISC_ICON} fluid className="ml-auto" />
            </Row>
             
            <Row className="d-flex">
                <Col className="d-flex flex-column">
                    <Image src={PROFILE_PHOTO} fluid className="m-auto" />
                     
                    <div className="d-flex" style={{zIndex: 1, marginTop: '-20px'}}>
                        <Col lg={6} />

                        <Col lg={6}>
                            <Image src={INSTA_ICON} fluid className="m-auto" />
                        </Col>
                    </div>
                </Col>
            </Row>

            <Row className="d-flex mb-3 mt-3">
                <p className="m-auto pages-card-title" >Instagram page #01</p>
            </Row>

            <Row className="d-flex mt-3">
                <Row className="m-auto justify-content-between">
                     
                    <Col lg={6}>
                        <Col>
                            <p className="m-auto pages-card-title" style={{fontSize: '35px'}}>34.7k</p>
                        </Col>

                        <Col className="d-flex" >
                            <p className="m-auto pages-card-desc-2">Fans</p>
                        </Col>
                    </Col>

                        <Col lg={6}>
                            <Col>
                                <p  className="m-auto pages-card-title" style={{fontSize: '35px'}}>416k</p>
                            </Col>

                            <Col className="d-flex">
                                <p  className="m-auto pages-card-desc-2">Interactions</p>
                            </Col>
                        </Col>
                </Row>
            </Row>
        </div>
    );

    // <img src={'https://graph.facebook.com/v3.2/' + props.page.idPage + '/picture?access_token=' + props.page.access_token} alt="page thumbnail" style={{ borderRadius: 50 + "%" }} />
      
    return (
        <>
        <Row>
            <p className="home-big-title">Pages Connected</p>
        </Row>

        <Row>
            {
            props.fbData
            ?
                (
                    <>
                    {Object.keys(props.fbData).map( (key, index) => 
                        <h1>{props.fbData[key][0].name}</h1>
                    )}
                    <OwlCarousel margin={10} items={4}>
                        {/* {Object.keys(props.fbData).map( (key, index) =>  */}
                            <div>{renderPageCard()}</div>
                            <div>{renderPageCard()}</div>
                            <div>{renderPageCard()}</div>
                        {/* )} */}
                    </OwlCarousel>
                    
                    </>
                )
            :
                (
                <Col className="d-flex">
                    <h1 className="mr-auto">LOADING......</h1>
                </Col>
                )
            }
            
        </Row>
        </>
    );
}


const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
    }
};

export default connect(mapStateToProps, { getFbData, getInstaData })(Pages);
