import { useMediaQuery } from 'react-responsive';

export const host = 'https://node3.mobilesupportbot.com:5075';

export const trainAgentHost = 'https://node2.mobilesupportbot.com:5051';

// For responsive design
export const isDesktop = () => {
    const Desktop = useMediaQuery({ minWidth: 992 });
    return Desktop ? true : false;
};

export const isTablet = () => {
    const Tablet = useMediaQuery({ minWidth: 768, maxWidth: 991 });
    return Tablet ? true : false;
};

export const isMobile = () => {
    const Mobile = useMediaQuery({ maxWidth: 767 });
    return Mobile ? true : false;
};
