const initialState = {
    wizardStep : 0,
}

const wizardReducer = (state = initialState, action) => {

    
    switch (action.type) {
        case 'NEXT_WIZARD_STEP' :
            return {
                ...state,
                wizardStep : state.wizardStep + 1,
            }
     
        default:
            return state;
    }
}

export default wizardReducer;
