import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { resetSocialMediaSelections } from '../../../redux/actions/socialMediaActions';

import Pages from '../Pages/Pages';

import {
    Col,
    Row,
} from 'react-bootstrap';

import { FaTrashAlt, FaInfoCircle } from 'react-icons/fa';

export const Team = (props) => {

    const [showPopover, setShowPopover] = useState(false);


    useEffect(() => {
        props.resetSocialMediaSelections();
    },[]);


    const handlePopover = (delay) => {
        setTimeout( () => {
          setShowPopover(!showPopover);
        },delay);
    };

    const renderPopover = () => (
        <div className="d-flex manage-products-popover">
          <p className="m-auto">
            Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s
          </p>
        </div>
    );

    return (
        <div style={{height: window.innerHeight * 1.2}}>

            <Row className="mt-5">
                <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Team</p>
                <FaInfoCircle color={"#4080FF"} size={'15'} onMouseEnter={() => handlePopover('20')} onMouseLeave={() => handlePopover('300')} />
                { showPopover && renderPopover() }
            </Row>

            <Pages title="Select a page to manage team" titleStyling={{color: '#4D4F5C', fontSize: '16px', fontFamily: 'Poppins Medium' }} />

            <Row className="mt-5 mr-0">                
                <Col lg={10} className="p-3" style={{backgroundColor: 'white', borderRadius: '0.3rem'}}>
                    <div className="d-flex mx-0 w-100 pt-3 pb-3 pr-2 pl-2" style={{borderBottom: '1px solid #EBEDF0'}}>
                        <div className="mr-auto d-flex">
                            <p className="my-auto manage-products-table-title">Name</p>
                        </div>

                        <div className="d-flex m-auto">
                            <p className="mr-5 my-auto manage-products-table-title">Role</p>
                        </div>

                        <div className="d-flex ml-auto">
                            <p className="mr-5 my-auto manage-products-table-title">Actions</p>
                        </div>
                    </div>
                    
                    <div className="d-flex mx-0 w-100 pt-3 pb-3 " style={{ borderBottom: '1px solid #EBEDF0' }}>
                        <div className="mr-auto d-flex">
                            <p className="my-auto manage-product-entity-name">Nadia Mkhinini</p>
                        </div>

                        <div className="d-flex m-auto">
                            <p className="mr-5 my-auto manage-product-entity-name">Owner</p>
                        </div>

                        <div className="d-flex ml-auto">
                            <FaTrashAlt className="ml-5 my-auto" color={'#E5007D'} size={'25'} />
                        </div>
                    </div>
                </Col>
                        
                <Col lg={2} />
            </Row>
        </div>
    )
}

const mapStateToProps = (state) => ({
    socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
})

export default connect(mapStateToProps, { resetSocialMediaSelections, })(Team)
