import React, { useEffect, useState } from 'react'

import {
    Row,
    Col,
    Image,
    Table,
    Spinner,
} from 'react-bootstrap';
import Lottie from 'react-lottie';


import { connect } from 'react-redux';
import { nextWizardStep, getWizardPagesPosts, getConnectedPagesProject } from '../../../redux/actions/wizardActions';

import animationData from '../../../assets/json/loading.json';

const PROJECT_PHOTO = require('../../../assets/images/home/project-photo.png');
const DISCONNECTED_ICON = require('../../../assets/images/home/disconnected-icon.png');

function WizardPosts(props) {
    
    const tableTitles = [
        {title : 'Post'},
        {title : 'Type'},
        {title : 'Published'},
        {title : 'Agent Status'},
    ];

    const [loading, setLoading] = useState(true);
   
    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    useEffect(() => {
        props.getWizardPagesPosts(props.wizardSelectedPage);
        props.getConnectedPagesProject();
    }, []);

    useEffect(() => {
        console.log("Connected =>", props.wizardActivePagePosts);
    });

    return (
    <Row className="d-flex mt-5">  
        <Col lg={1} />

        <Col className="d-flex flex-column wizard-pages-container" style={{backgroundColor: 'white'}}>
            <Row className="d-flex mt-4 ml-2">
                <p className="my-auto wizard-pages-title">Select one post to active the iModo moderation.</p>
            </Row>
            
            {/* POSTS LIST */}
            <Row  className="mt-5 mb-5">
                <Col lg={1} />

                <Col>
                    {props.wizardPagePosts
                    ?
                        (
                        <Table responsive>
                            <thead>
                                <tr>
                                    <th></th>
                                    {tableTitles.map(item => 
                                        <th>{item.title}</th>    
                                    )}
                                </tr>
                            </thead>
                            <tbody>
                            {
                                props.wizardPagePosts.map( (post,index) => {
                                    let createdTime = new Date(Date.parse(post.created_time));
                                    let publishedDate = `${createdTime.getDate()}/${createdTime.getMonth()}/${createdTime.getFullYear()}`;
                                    let checkedMinutes = createdTime.getMinutes() < 10 ? "0" + createdTime.getMinutes() : createdTime.getMinutes();
                                    let publishedTime = `${createdTime.getHours()}:${checkedMinutes}`
                                
                                    return (
                                        <tr key={index}>
                                            <td></td>
                                            <td>
                                                <Row className="d-flex">
                                                    <Image src={PROJECT_PHOTO} className="my-auto ml-3" />
                                                    <p className="my-auto ml-2">{post.message}</p>
                                                </Row>
                                            </td>

                                            <td>
                                                <Image src={DISCONNECTED_ICON} className="mt-2 ml-2" />
                                            </td>

                                            <td>
                                                <div className="flex-column">
                                                    <p className="m-auto my-agent-project-date">{publishedDate}</p>
                                                    <p  className="m-auto my-agent-project-time">{publishedTime}</p>
                                                </div>
                                            </td>

                                            <td>
                                                <div className="d-flex">
                                                    <p className="ml-4">{isPostActive() ? "Active" : "Not Active"}</p>
                                                </div>
                                            </td>

                                        </tr>
                                    )
                                } 
                                )
                            }
                            </tbody>
                        </Table>
                        )
                    : 
                        (
                            <Row className="d-flex">
                                <div className="m-auto">
                                    <Lottie options={defaultOptions} width={200} /*height={400}*/ />
                                </div>
                            </Row>
                        )
                    }
              
                </Col>

                <Col lg={1} />
            </Row>


            <Row className="d-flex justify-content-between pl-4 pr-4">
                <div className="wizard-pages-inactive-btn mb-3">
                    Back
                </div>

                <div className="mb-3">
                    <Row>
                        <div className="wizard-pages-inactive-btn mr-3">
                            Save Draft
                        </div>

                        <div className="wizard-pages-active-btn" onClick={() => props.nextWizardStep()}>
                            Next
                        </div>
                    </Row>
                </div>
            </Row>

        </Col>

        <Col lg={1} />
    </Row>
    )
}


const mapStateToProps = (state) => {
    return {
        wizardStep: state.wizardR.wizardStep,
        wizardSelectedPage: state.wizardR.wizardSelectedPage,
        wizardPagePosts: state.wizardR.wizardPagePosts,
        wizardActivePagePosts: state.wizardR.wizardActivePagePosts,
    }
};

export default connect(mapStateToProps, { nextWizardStep, getWizardPagesPosts, getConnectedPagesProject })(WizardPosts);
