const initialState = {
    userdata: {},
    token: ""
}

const auth = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN':
            return { ...state, userdata: { ...action.user.data.data.user }, token: action.user.data.data.token }
        default:
            return state
    }
}
export default auth
