import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { resetSocialMediaSelections } from '../../../redux/actions/socialMediaActions';

import Pages from '../Pages/Pages';

import {
    Col,
    Row,
    Modal,
    Image,
} from 'react-bootstrap';

import { FaTrashAlt, FaInfoCircle } from 'react-icons/fa';
import { IoIosClose } from 'react-icons/io';
import Lottie from 'react-lottie';
import animationData from '../../../assets/json/loading.json';
const INSTA_ICON = require('../../../assets/images/home/insta-icon.png');
const FB_ICON = require('../../../assets/images/home/fb-icon.png');


export const Team = (props) => {

    const fbHost = "https://graph.facebook.com/v8.0";
    
    const [showPopover, setShowPopover] = useState(false);
    const [showInviteModal, setShowInviteModal] = useState(false);
    const [fbGraphData,setFbGraphData] = useState(null);

    const handleInviteModal = () => setShowInviteModal(!showInviteModal);


    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };
    
    
    const handlePopover = (delay) => {
        setTimeout( () => {
            setShowPopover(!showPopover);
        },delay);
    };
    
    const renderPopover = () => (
        <div className="d-flex manage-products-popover">
          <p className="m-auto">
            Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s
          </p>
        </div>
    );


    const renderModal = (page) => (
        <Modal show={showInviteModal} onHide={handleInviteModal} centered>
            <div className="d-flex flex-column">
                <IoIosClose className="ml-auto mr-3 mt-2" color={'#9F9F9F'} size={'28'} onClick={handleInviteModal} style={{cursor: 'pointer'}} />
                
                <Modal.Body>
                    <Row className="d-flex">
                        <Col className="d-flex flex-column m-auto">
                            {
                                props.picture !== null
                                ?
                                <Image src={props.picture} fluid className="m-auto pages-card-profile-picture" />
                                :
                                fbGraphData !== null 
                                ?
                                <Image src={fbGraphData.picture.data.url} fluid className="m-auto pages-card-profile-picture" />
                                :
                                <Lottie options={defaultOptions} width={150} />
                            }
                                
                            <div className="d-flex" style={{ zIndex: 1, marginTop: '-20px' }}>
                                <Col lg={6} />

                                <Col lg={6}>
                                    <Image src={props.platform === "fb" ? FB_ICON : INSTA_ICON} fluid className="m-auto" />
                                </Col>
                            </div>
                        </Col>
                    </Row>

                </Modal.Body>

            
            </div>
        </Modal>
    );

    useEffect(() => {
        props.resetSocialMediaSelections();
        
        if(props.socialMediaPageSelected.platform === "fb") {
            axios.get(`${fbHost}/${props.socialMediaPageSelected.id}/?fields=fan_count,picture,new_like_count&access_token=${props.socialMediaPageSelected.accessToken}`)
                .then(res => { setFbGraphData(res.data); })
                .catch(err => { console.log("Team fb",(err)) })
            } else {
            // INSTA
            axios.get(`${fbHost}/${props.socialMediaPageSelected.id}/?fields=id,media,followers_count,media_count,profile_picture_url,username,name&access_token=${props.socialMediaPageSelected.accessToken}`)
                .then(res => { setInstaGraphData(res.data); })
                .catch(err => { console.log("team Insta",(err)) })
        };

    },[]);

    return (
        <div style={{height: window.innerHeight * 1.2}}>

            {renderModal(props.socialMediaPageSelected)}

            <Row className="mt-5">
                <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Team</p>
                <FaInfoCircle color={"#4080FF"} size={'15'} onMouseEnter={() => handlePopover('20')} onMouseLeave={() => handlePopover('300')} />
                { showPopover && renderPopover() }
            </Row>

            <Pages title="Select a page to manage team" titleStyling={{color: '#4D4F5C', fontSize: '16px', fontFamily: 'Poppins Medium' }} />

            {props.socialMediaPageSelected &&
            <Row className="mt-5 mr-0">                
                <Col lg={10} className="p-3" style={{backgroundColor: 'white', borderRadius: '0.3rem'}}>
                    {props.socialMediaPageSelected.team.length > 0
                    ?
                    <>

                    <Row className="mb-3"> 
                        <Col lg={12} className="d-flex">
                            <div className="team-invite-btn mr-auto" onClick={() => setShowInviteModal(true)}>
                                Invite Member
                            </div>
                        </Col>
                    </Row>
                    <Row>
                        <Col lg={4} className="d-flex pt-3 pb-3" >
                            <p className="mr-auto my-auto manage-products-table-title">Name</p>
                        </Col>
                        
                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="m-auto manage-products-table-title">Role</p>
                        </Col>

                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="ml-auto my-auto manage-products-table-title">Actions</p>
                        </Col>
                    </Row>

                    {props.socialMediaPageSelected.team.map(member =>     
                    <Row key={member.idFacebook+Math.random()}>
                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="my-auto manage-product-entity-name">{member.first_name} {member.last_name}</p>
                        </Col>
                        
                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <p className="m-auto manage-product-entity-name">{member.role}</p>
                        </Col>

                        <Col lg={4} className="d-flex pt-3 pb-3">
                            <FaTrashAlt className="ml-auto my-auto mr-3" color={'#E5007D'} size={'25'} style={{ cursor: 'pointer' }} />
                        </Col>
                    </Row>
                    )}
                    </>
                    :
                    <Row>you have 0 team member for now...</Row>
                    }
                
                </Col>
                        
                <Col lg={2} />
            </Row>            
            }
        
        </div>
    )
}

const mapStateToProps = (state) => ({
    socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
})

export default connect(mapStateToProps, { resetSocialMediaSelections, })(Team)
