import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux';
import { nextWizardStep, getAllIntents, addProject } from '../../../redux/actions/wizardActions';

import WizardIntentDetails from './WizardIntentDetails';

import {
    Row,
    Col,
    // Popover,
    // Form,
    // Image,
} from 'react-bootstrap';

import {
    Link
} from 'react-router-dom';


// import { FaTrashAlt } from 'react-icons/fa';
// import { IoIosArrowDown } from 'react-icons/io';
// import { AiOutlineCloseCircle } from 'react-icons/ai';
// import { MdAddCircleOutline } from 'react-icons/md';

export const WizardConfig = (props) => {

    const [selectedIntents, setSelectedIntents] = useState(null);

    useEffect(() => {
        props.getAllIntents(props.wizardIntentType);
    },[]);

    useEffect(() => {
        console.log("WizardConfig intents :", props.wizardIntents);
    }, [props.wizardIntents])

    // const popover = (
    //     <Popover id="popover-basic">
    //       <div>
    //           FREE POPOVER :3
    //       </div>
    //     </Popover>
    //   );

    const addNewIntent = (intent,index) => {
        let tabToPush = selectedIntents;
        tabToPush.push({intent: intent, index: index});
        setSelectedIntents(tabToPush);
    };

    useEffect(() => {
            console.log(selectedIntents);
    },[selectedIntents]);

    return (
        <Row className="mt-5">  
            <Col lg={1} />
    
            <Col className="wizard-pages-container mb-3" style={{backgroundColor: 'white'}}>
                
                {/* INTENTS */}
                <Row className="d-flex">
                    <Col lg={6} className="d-flex flex-column p-3">
                        
                        <div className="d-flex flex-column wizard-config-intents-container">
                            <p className="mr-auto ml-3 p-2 wizard-config-intent-title">Select Intents</p>
                            
                            <Row className="ml-1 mr-1 mb-3">
                                {
                                props.wizardIntents 
                                &&
                                props.wizardIntents.map((intent, index) => (
                                    <Col lg={3} className="d-flex ml-4 mb-3 pt-2 pb-2 wizard-config-intent-box" onClick={() => addNewIntent(intent, index)}>
                                        <p className="m-auto wizard-config-intent-name">{intent.intent}</p>
                                    </Col>
                                ))}
                            </Row>
                        </div>
                        
                    </Col>

                    <Col />
                </Row>
                
                {selectedIntents &&
                    selectedIntents.map((intent,index) => 
                    <WizardIntentDetails index={index} intent={intent} />
                )}
             

                {/* BUTTONS */}
                <Row className="d-flex justify-content-between pl-4 pr-4">
                    <div className="wizard-pages-inactive-btn mb-3">
                        {/* MUST RESET ALL WIZARD VARS */}
                        <Link to="/home/pages" style={{textDecoration: 'none', color: '#818E94'}} onClick={() => props.resetWizardStep()} >
                            Back
                        </Link>
                    </div>

                    <div className="mb-3">
                        <Row>
                            <div className="wizard-pages-inactive-btn mr-3">
                                Save Draft
                            </div>

                            <div className="wizard-pages-active-btn" onClick={() => props.nextWizardStep()}>
                                Next
                            </div>
                        </Row>
                    </div>
                </Row>

            </Col>
    
            <Col lg={1} />
        </Row>
        )
}

const mapStateToProps = (state) => ({
    wizardStep: state.wizardR.wizardStep,
    wizardIntentType: state.wizardR.wizardIntentType,
    wizardIntents: state.wizardR.wizardIntents,
})

export default connect(mapStateToProps, {nextWizardStep, getAllIntents, addProject})(WizardConfig)
