const initialState = []

const allPages = (state = initialState, action) => {
    switch (action.type) {
        case 'GETALLPAGES':
            return { ...state, ...action.pages.data.data }
        case 'UPDATEPAGE':
            let indexUpdatedState = Object.keys(state).map((el, index) => {
                if (state[el].id == action.page.data.data.id) {
                    return index
                }
            })
            return { ...state, indexUpdatedState: action.page.data.data }
        default:
            return state
    }
}
export default allPages
