import React, { useEffect, useState } from 'react'
import '../Home.css';
import {
    Row,
    Col,
    FormControl,
    Dropdown
} from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import Slider from "react-slick";
import Lottie from 'react-lottie';

import { connect } from 'react-redux';
import { selectSocialMediaPage, getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import PageCard from './PageCard';

import animationData from '../../../assets/json/loading.json';

function Pages(props) {

    const [loadingPages, setLoadingPages] = useState(true);
    const [showFacebook, setShowFacebook] = useState(true);
    const [showInstagram, setShowInstagram] = useState(true);

    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    const settings = {
        dots: true,
        infinite: false,
        speed: 700,
        slidesToShow: 3.7,
        slidesToScroll: 3,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
    };

    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a
          href=""
          ref={ref}
          onClick={(e) => {
            e.preventDefault();
            onClick(e);
          }}
        >
          {children}
          &#x25bc;
        </a>
    ));

    const CustomMenu = React.forwardRef(
        ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
          const [value, setValue] = useState('');
      
          return (
            <div
              ref={ref}
              style={style}
              className={className}
              aria-labelledby={labeledBy}
            >
              <FormControl
                autoFocus
                className="mx-3 my-2 w-auto"
                placeholder="Type to filter..."
                onChange={(e) => setValue(e.target.value)}
                value={value}
              />
              <ul className="list-unstyled">
                {React.Children.toArray(children).filter(
                  (child) =>
                    !value || child.props.children.toLowerCase().startsWith(value),
                )}
              </ul>
            </div>
          );
        },
      );

    useEffect(() => {
        props.getFbData();
        props.getInstaData();
        setTimeout(() => {
            setLoadingPages(false);
        },2000);
    },[]);

    useEffect(() => {
        if(props.fbData) {
            console.log("Pages fb data =>", props.fbData);
        }
    },[props.fbData]);
    
    useEffect(() => {
        if(props.instaData) {
            console.log("Pages insta Data =>", props.instaData);
        }
    },[props.instaData]);

    return (
    <>
        <Row>
            <Col lg={6} className="d-flex" style={{backgroundColor: 'red'}}>
                {
                props.title 
                ?
                    <p className="mr-auto home-big-title" style={props.titleStyling ? props.titleStyling : {} }>{props.title}</p>
                :
                    <p className="mr-auto home-big-title">Pages Connected</p>
                }
            </Col>

            <Col lg={5} className="d-flex" style={{backgroundColor: 'yellow'}}>
                <div className="ml-auto">
                    <Dropdown>
                        <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
                        Custom toggle
                        </Dropdown.Toggle>

                        <Dropdown.Menu as={CustomMenu}>
                        <Dropdown.Item eventKey="1">Red</Dropdown.Item>
                        <Dropdown.Item eventKey="2">Blue</Dropdown.Item>
                        <Dropdown.Item eventKey="3" active>
                            Orange
                        </Dropdown.Item>
                        <Dropdown.Item eventKey="1">Red-Orange</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
            </Col>

            <Col lg={1} />
            
        </Row>

        <Row>

            {
            !loadingPages
            ?
                (
                    <>

                    {props.fbData && props.instaData && props.fbData.length + props.instaData.length > 3
                    ?
                    (
                    <div style={{ width: '95%', height: '400px' }}>
                        <Slider {...settings}>
                            {
                            props.fbData !== null && showFacebook &&
                                props.fbData.map((page,index) => 
                                    <div key={index} onClick={() => props.selectSocialMediaPage(page,"fb",page.status)}>
                                        <PageCard 
                                            platform={"fb"}
                                            isConnectedPage={page.isConnected} 
                                            namePage={page.name} 
                                            idPage={page.idPage} 
                                            picture={null} 
                                            accessToken={page.access_token} 
                                            status={page.status} 
                                            team={page.user} 
                                            admins={page.admins} 
                                            setLoadingPages={setLoadingPages} 
                                        />
                                    </div>
                                )
                            }
                            
                            {
                            props.instaData !== null && showInstagram &&
                                props.instaData.map((page,index) => 
                                    <div key={index} onClick={() => props.selectSocialMediaPage(page,"insta",page.status)}>
                                        <PageCard 
                                            platform={"insta"} 
                                            isConnectedPage={page.isConnected}
                                            namePage={page.name} 
                                            idPage={page.idPage} 
                                            picture={page.imageUrl} 
                                            accessToken={page.access_token} 
                                            status={page.status} 
                                            team={page.user} 
                                            admins={page.admins} 
                                            setLoadingPages={setLoadingPages}
                                        />
                                    </div>
                                )
                            }
                        </Slider>
                    </div>
                    )
                    :
                    (
                    <Row className="d-flex">
                        {
                        props.fbData !== null &&
                            props.fbData.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"fb",page.status)}>
                                    <PageCard 
                                        platform={"fb"}
                                        isConnectedPage={page.isConnected} 
                                        namePage={page.name} 
                                        idPage={page.idPage} 
                                        picture={null} 
                                        accessToken={page.access_token} 
                                        status={page.status} 
                                        team={page.user} 
                                        admins={page.admins} 
                                        setLoadingPages={setLoadingPages} 
                                    />
                                </div>
                            )
                        }
                            
                        {
                        props.instaData !== null &&
                            props.instaData.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"insta",page.status)}>
                                    <PageCard 
                                        platform={"insta"} 
                                        isConnectedPage={page.isConnected}
                                        namePage={page.name} 
                                        idPage={page.idPage} 
                                        picture={page.imageUrl} 
                                        accessToken={page.access_token} 
                                        status={page.status} 
                                        team={page.user} 
                                        admins={page.admins} 
                                        setLoadingPages={setLoadingPages}
                                    />
                                </div>
                            )
                        }
                    </Row>
                    )

                    }
                    </>
                )
            :
                (
                <div className="d-flex" style={{ width: '100%' }}>
                    <div className="m-auto">
                        <Lottie options={defaultOptions} width={200} />
                    </div>
                </div>
                )
            }
        </Row>
    </>
    );
}

const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { selectSocialMediaPage,getFbData, getInstaData })(Pages);
