import React from 'react';
import './Login.css';
import { withRouter } from 'react-router-dom';
import { FacebookProvider, LoginButton } from 'react-facebook';
import { connect } from 'react-redux';
import { loginAction, fbdata } from '../../actions'
import axios from 'axios';
import { showLoading, hideLoading } from 'react-redux-loading-bar';

import {host} from '../../config';

function Facebook(props) {

  const handleResponse = (data) => {
    // props.fbdata(data)
    // props.login(data.tokenDetail.accessToken)

    // console.log('facebookdata ', data)

    let token = data.tokenDetail.accessToken;

    axios.post(host + '/api/v1/auth/login/facebook', {
        token
    }).then(resdata => {
        console.log('Login =>', resdata)

        // localStorage.setItem('authtoken', resdata.data.data.user.idFacebook)

        // dispatch(hideLoading())

        // ownProps.history.push('dashboard')

        // dispatch({
        //     type: 'LOGIN',
        //     user: resdata
        // })
    }).catch((err) => console.log("Login Error =>",err) )

  }

  const handleError = (error) => {
    console.log(error);
  }

  return (
    // <div className="App">
      <FacebookProvider appId="783983335073350" version="v3.0">
        <LoginButton
          scope="public_profile,publish_pages,email,pages_messaging,pages_manage_cta,pages_show_list,manage_pages,instagram_basic,instagram_manage_comments"
          onCompleted={handleResponse}
          onError={handleError}
          className="facebookbtn"
        >
          <span>{props.content.toUpperCase()}</span>
        </LoginButton>
      </FacebookProvider>
    // </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.userdata || {}
})

const mapDispatchToProps = (dispatch, owenProps) => {
  return {
    login: (token) => dispatch(loginAction(token, owenProps)),
    fbdata: (data) => dispatch(fbdata(data))
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Facebook));
