import axios from 'axios'
import { host } from '../config'
import { showLoading, hideLoading } from 'react-redux-loading-bar'


export const loginAction = (token, owenProps) => {
    return (dispatch) => {
        dispatch(showLoading())
        return axios.post(host + '/api/v1/auth/login/facebook', {
            token
        }).then(resdata => {
            console.log('action ', resdata)
            localStorage.setItem('authtoken', resdata.data.data.token)
            dispatch({
                type: 'LOGIN',
                user: resdata
            })
            owenProps.history.push('dachboard/')
            dispatch(hideLoading())
        }).catch(err => console.log('loginAction ', err))
    }
}


export const getAllPagesAction = () => {
    return (dispatch) => {
        return axios.get(host + '/secure/pages/get/all', {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
            .then(res => {
                console.log('pages ', res)
                dispatch({
                    type: 'GETALLPAGES',
                    pages: res
                })
            })
    }
}
