import React from 'react'
import './AutoWizard.css';

import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';


import WizardSteps from './WizardSteps';
import WizardPages from './WizardPages';
import WizardPosts from './WizardPosts';
import WizardConfig from './WizardConfig';
import WizardTest from './WizardTest';

// Redux:
import { connect } from 'react-redux';
import { nextWizardStep } from '../../../redux/actions/wizardActions';

const INSTA_ICON = require('../../../assets/images/home/insta-icon.png');
const FB_ICON = require('../../../assets/images/home/fb-icon.png');


function AutoWizard(props) {

    return (
        <Col xs={12} style={{height: window.innerHeight * 2}} className="d-flex flex-column" >
            

            {/* SELECTED PAGE */}
            {/* { 
            props.wizardSelectedPage !== null &&
            <Row className="mb-3" style={{background: 'red'}}>
                <Col lg={1} />
                
                <Col lg={10} className="d-flex p-0" style={{background: 'cyan'}}>
                    <Row className="wizard-selected-page-container d-flex mr-auto p-3">
                        <div className="d-flex flex-column mr-3">
                            <Image src={props.wizardSelectedPage.picture_url} className="m-auto" style={{maxWidth: '40px'}} />
                            <Image src={props.wizardSelectedPage.platform === "instagram" ? INSTA_ICON : FB_ICON} className="ml-auto" style={{zIndex: 1, marginTop: '-10px'}}/>
                        </div>
                        <p className="m-auto wizard-pages-page-title">{props.wizardSelectedPage.name}</p> 
                    </Row>
                </Col>
                
                <Col lg={1} />
            </Row>
            } */}
            
            {/* SELECTED POST */}
            {/* { 
            props.wizardSelectedPost !== null &&
            <Row className="mr-auto d-flex">
                <Row className="wizard-selected-page-container m-auto p-3">    
                    <div className="d-flex flex-column mr-3">
                        <Image src={props.wizardSelectedPost.picture ? props.wizardSelectedPost.picture : props.wizardSelectedPage.picture_url} className="m-auto" style={{maxWidth: '40px'}} />
                        <Image src={props.wizardSelectedPage.platform === "instagram" ? INSTA_ICON : FB_ICON} className="ml-auto" style={{zIndex: 1, marginTop: '-10px'}}/>
                    </div>
                    <p className="m-auto wizard-pages-page-title">
                        {props.wizardSelectedPost.message && props.wizardSelectedPost.message.length > 40 
                        ? 
                        props.wizardSelectedPost.message.substring(0,37) + "..." 
                        :
                        props.wizardSelectedPost.message
                        ?
                        props.wizardSelectedPost.message
                        :
                        props.wizardSelectedPage.name
                        }
                    </p>  
                </Row>
            </Row>
            } */}

            <Row className="d-flex" style={{background: 'cyan'}}>

                <Col lg={1} />
                
                <Col lg={3} className="d-flex flex-column">
                    { 
                    props.wizardSelectedPage !== null &&
                    <Row className="d-flex mr-auto px-3 py-1 mb-3">
                        <div className="d-flex flex-column mr-3">
                            <Image src={props.wizardSelectedPage.picture_url} className="m-auto" style={{maxWidth: '40px', borderRadius: '50%'}} />
                            <Image src={props.wizardSelectedPage.platform === "instagram" ? INSTA_ICON : FB_ICON} className="ml-auto" style={{zIndex: 1, marginTop: '-10px'}}/>
                        </div>
                        <p className="m-auto wizard-pages-page-title">{props.wizardSelectedPage.name}</p> 
                    </Row>
                    }

                    { 
                    props.wizardSelectedPost !== null &&
                    <Row className="d-flex mr-auto px-3 py-1" style={{ background: '#fff' }}>
                        <div className="d-flex flex-column mr-3">
                            <Image src={props.wizardSelectedPost.picture ? props.wizardSelectedPost.picture : props.wizardSelectedPage.picture_url} className="m-auto" style={{maxWidth: '40px'}} />
                        </div>
                        <p className="m-auto wizard-pages-page-title">
                            {props.wizardSelectedPost.message && props.wizardSelectedPost.message.length > 40 
                            ? 
                            props.wizardSelectedPost.message.substring(0,37) + "..." 
                            :
                            props.wizardSelectedPost.message
                            ?
                            props.wizardSelectedPost.message
                            :
                            props.wizardSelectedPage.name
                            }
                        </p>  
                    </Row>
                    }

                </Col>

                <Col lg={4} className="d-flex p-0" style={{background: 'cyan'}}>
                    <p className="wizard-title m-auto">iModo Wizard</p> 
                </Col>

                <Col lg={4} style={{background: 'yellow'}} />
            </Row>

            
            <WizardSteps />
            
            {props.wizardStep === 0 && <WizardPages />}

            {props.wizardStep === 1 && <WizardPosts />}

            {props.wizardStep === 2 && <WizardConfig />}

            {props.wizardStep === 3 && <WizardTest />}

        </Col>
    )
}

const mapStateToProps = (state) => {
    return {
        wizardStep: state.wizardR.wizardStep,
        wizardSelectedPage: state.wizardR.wizardSelectedPage,
        wizardSelectedPost: state.wizardR.wizardSelectedPost,
    }
};

export default connect(mapStateToProps, { nextWizardStep })(AutoWizard);
