import React, { useEffect } from 'react';
import Pages from '../Pages/Pages';

import moment from 'moment';

import { connect } from 'react-redux';                       
import { getDashboard, getDashboardPosts, resetSocialMediaSelections, resetDashboard } from '../../../redux/actions/socialMediaActions';

import {
    Col,
    Row,
    Image,
} from 'react-bootstrap';

import { PieChart } from 'react-minimal-pie-chart';

function Dashboard(props) {
    
    let {
        socialMediaPageSelected,
        dashboardData,
        dashboardPosts,


        // FUNCTIONS
        getDashboard,
        getDashboardPosts,
        resetSocialMediaSelections,
        resetDashboard,
    } = props;


    const kFormatter = (num) => {
        return Math.abs(num) > 999 ? Math.sign(num)*((Math.abs(num)/1000).toFixed(1)) + 'k' : Math.sign(num)*Math.abs(num)
    };
    
    useEffect(() => {
        resetDashboard();
        resetSocialMediaSelections();
    },[]);

    useEffect(() => {
        if(socialMediaPageSelected) {
            getDashboard(socialMediaPageSelected);
            getDashboardPosts(socialMediaPageSelected);
        }
        return () => {

        };
    }, [socialMediaPageSelected]);

    return (
        <div style={{ height: window.innerHeight * 2, marginTop: '135px' }}>
            
            <Pages />

            {socialMediaPageSelected 
            &&
            <>
            <Row>
                <p className="home-big-title" style={{ fontSize: '20px', fontFamily: 'Poppins SemiBold' }}>Most requested intents</p>
            </Row>
            
            {dashboardData.length > 0
            &&
            <Row>
                <Col lg={10} className="p-4" style={{background: 'white'}}>
                    <Row>
                        <Col lg={8}>
                            <Row className="justify-content-between mx-2">
                                <p>Intents</p>
                                <p>Total</p>
                            </Row>

                            {/* LINE */}
                            {dashboardData.map((data,i) => 
                            i < 5 &&
                            <>
                            <Row key={i} className="justify-content-between mx-2">
                                <span className="intent-dashboard d-flex" style={{background: data.color}}> 
                                    <p className="m-auto px-4">{data.title}</p>
                                </span>

                                <span className="d-flex">
                                    <p className="intent-dashboard-total m-auto">{data.value}</p>
                                </span>
                            </Row>
                            <hr className="intent-dashboard-separator" />
                            </>
                            )}
                        </Col>

                        <Col lg={4} className="d-flex">
                            <div className="m-auto" style={{height: '200px', width: '200px'}}>
                                <PieChart lineWidth={20} data={dashboardData} />
                            </div>
                        </Col>

                    </Row>
                </Col>
            </Row>
            }

            <Row className="mt-4 mb-3">
                <p className="home-big-title" style={{ fontSize: '20px', fontFamily: 'Poppins SemiBold' }}>Modo Posts</p>
            </Row>
            
            {dashboardPosts.length > 0 
            ? 
            <Row>
                <Col lg={10}>
                    <Row>
                        {dashboardPosts.map(post =>
                            <div className="dashboard-post-card d-flex my-2 mr-4" key={post.idPost}>
                                <Row className="d-flex">

                                    <Col lg={12} className="p-3 mb-auto">
                                        <Row>
                                            <Col lg={2} className="d-flex">
                                                <Image src={socialMediaPageSelected.picture_url} className="my-auto ml-2" style={{height: '30px', width: '30px', borderRadius: '50%'}} />
                                            </Col>

                                            <Col lg={10}>
                                                <p>{socialMediaPageSelected.name}</p>
                                                <span>{post.post.created_time}</span>
                                            </Col>
                                        </Row>
                                    </Col>

                                    <Col lg={12} className="d-flex m-auto">
                                    {
                                    post.post.hasOwnProperty('full_picture')
                                    ?
                                    // POST IMAGE
                                    <div className="dashboard-post-card-full-image m-auto" style={{ backgroundImage: `url(${post.post.full_picture})` }} />
                                    :
                                    post.post.hasOwnProperty('message')
                                    &&
                                    // POST MESSAGE
                                    <p className="px-4">{post.post.message.length > 250 ? post.post.message.substring(0,250) + "..." : post.post.message}</p>
                                    }
                                    </Col>

                                    <Col lg={12} className="mt-auto py-2">
                                        <Row className="px-4 d-flex" >
                                            <div className="flex-column d-flex mr-auto">
                                                <p className="m-auto">{post.hasOwnProperty('comments') && kFormatter(post.comments)}</p>
                                                <span className="m-auto">Comments</span>
                                            </div>
                                            <div className="flex-column d-flex m-auto">
                                                <p className="m-auto">{post.hasOwnProperty('matched') && kFormatter(post.matched)}</p>
                                                <span className="m-auto">Matched</span>
                                            </div>
                                            <div className="flex-column d-flex ml-auto">
                                                <p className="m-auto">{post.hasOwnProperty('performance') && kFormatter(post.performance.toFixed(0)) + "%"}</p>
                                                <span className="m-auto">Performance</span>
                                            </div>

                                        </Row>
                                    </Col>

                                </Row>
                            </div>
                        )}
                    </Row>
                </Col>
            </Row>
            : 
            <p>NO POSTS</p>
            }

            </>
            }
        </div>
    )
}


const mapStateToProps = (state) => {
    return {
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
        dashboardData : state.socialMediaR.dashboardData,
        dashboardPosts : state.socialMediaR.dashboardPosts,
    }
};

export default connect(mapStateToProps, { getDashboard, getDashboardPosts, resetSocialMediaSelections, resetDashboard })(Dashboard);
