import React, { useEffect, useState } from 'react';

import  {
    Col,
    Row,
    Image,
    Form
} from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import { connect } from 'react-redux';
import { getLogs, answerComment} from '../../../redux/actions/myAgentsActions';
import moment from 'moment';
import Lottie from 'react-lottie';


import animationData from '../../../assets/json/loading.json';
import { BiSearch,BiShare } from 'react-icons/bi'; 
import { IoIosClose } from 'react-icons/io'; 
import { AiOutlineEllipsis, AiFillClockCircle } from 'react-icons/ai'; 
import { MdPhone, MdEmail } from 'react-icons/md'; 
import { FaBirthdayCake, FaHome } from 'react-icons/fa'; 
import { RiMapPin2Fill } from 'react-icons/ri'; 

const PROFILE_PHOTO = require('../../../assets/images/home/profile-photo.png');
const LETTER_ICON = require('../../../assets/images/home/3a.png');
const EMO_ICON = require('../../../assets/images/home/emo.png');
const BALISE_ICON = require('../../../assets/images/home/balise.png');


function MyAgentsMessages(props) {

    let {
        agentsSelectedProject,
        agentsCommentsMatched,
        agentsCommentsNotMatched,
        socialMediaPageSelected,


        // Functions:
        getLogs,
        answerComment,
    } = props;

    let history = useHistory();
    const [filter,setFilter] = useState('matched');
    const [loading, setLoading] = useState(true);
    const [choosenComment, setChoosenComment] = useState(null);
    const [answerChoosenComment, setAnswerChoosenComment] = useState('');
    // const [userData, setUserData] = useState({});

    const loadingAnimation = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };
    
    useEffect(() => {
        if(agentsSelectedProject) {
            getLogs(agentsSelectedProject.post.idPost)
            .then(() => {
                setLoading(false);
            });
        } else {
            history.push('/my-agents-messages');
        }
    },[]);

    const renderDate = (date) => {
        let newDate = moment(date).format("DD/MM/YYYY");
        let newTime = moment(date).format("h:mm");
        return `${newDate} ${newTime}`;
    };



    return (
        <div style={{marginTop: '100px'}}>
           <Row>
               <p className="my-agents-msgs-title" >Liste des messages rus</p>
           </Row>

           <Row className="mb-3">
               <Col lg={3}>
                    <Row className="justify-content-center">
                        <div className={filter === "matched" ? "my-agents-msgs-matched-btn" : "my-agents-msgs-not-matched-btn"} onClick={() => { setChoosenComment(null); setFilter('matched'); }}>
                            Matched
                        </div>
                        <div className={filter === "not-matched" ? "my-agents-msgs-matched-btn" : "my-agents-msgs-not-matched-btn"} onClick={() => {setChoosenComment(null); setFilter('not-matched'); }}>
                            Not Matched
                        </div>
                    </Row>
               </Col>

               <Col />
           </Row>
           
            {loading
            ?
            <div style={{ width: '100%', height: window.innerHeight }}>
                <div className="d-flex ">
                    <Lottie options={loadingAnimation} width={200} className="mx-auto" />
                </div>
            </div>
            :
            <Row>
                {/* BLOC 1 */}
                <Col lg={3} className="pt-3 pb-5" style={{backgroundColor : 'white',borderRadius: '0.5rem'}}>
                    <Row className="d-flex">
                        <BiSearch color={"#BCBCCB"} size={"20"} className="my-auto mr-2"  />
                        <Form.Control type="text" className="my-auto my-agents-search-input" placeholder="Search Message or Name" />
                    </Row>

                    {/* MATCHED */}
                    {filter === "matched" && agentsCommentsMatched && agentsCommentsMatched.length > 0 &&
                    agentsCommentsMatched.map((comment,indexComment) => 
                        
                        <div 
                            key={comment.comment_id+indexComment} 
                            className="pt-1 mt-3" 
                            style={{cursor: 'pointer'}}
                            onClick={() => setChoosenComment(comment)}
                        >
                            <Row className="d-flex p-4" style={{backgroundColor: '#F2F2F2'}}>
                                <Row className="mr-auto d-flex">
                                    <Image src={PROFILE_PHOTO} className={"m-auto"} style={{maxWidth: '50px'}} />
                                    <p className="my-auto ml-2 my-agents-msg-name">Haider</p>
                                </Row>

                                <Row className="ml-auto d-flex">
                                    <p className="m-auto my-agents-msg-date">{renderDate(comment.created_time)}</p>
                                </Row>
                            </Row>
                            
                            <Row className="d-flex pl-5 pr-5" style={{backgroundColor: '#F2F2F2'}}>
                                <p className="m-auto my-agents-msg-desc">{comment.message}</p>
                            </Row>
                            
                            <Row className="d-flex px-1" style={{backgroundColor: '#F2F2F2'}}>
                                {comment.intent && comment.intent.length > 0 &&
                                comment.intent.map((intent,index) => 
                                    <Col lg={12} className="d-flex my-1" key={index}>
                                        <p className="mr-auto my-auto my-agents-msg-intent">{intent.score * 100}% Intent: {intent.intent}</p>
                                    </Col>
                                )}
                                {/* <Row className="ml-auto d-flex">
                                    <input type="checkbox" className="my-auto" />
                                </Row>  */}
                            </Row>
                        </div>
                    )}
                    
                    {/* NOT MATCHED */}
                    {filter === "not-matched" && agentsCommentsNotMatched && agentsCommentsNotMatched.length > 0 &&
                    agentsCommentsNotMatched.map((comment,indexComment) =>
                        
                        <div 
                            key={comment.comment_id+indexComment} 
                            className="pt-1 mt-3" 
                            onClick={() => setChoosenComment(comment)}
                        >
                            <Row className="d-flex p-4" style={{backgroundColor: '#F2F2F2'}}>
                                <Row className="mr-auto d-flex">
                                    <Image src={PROFILE_PHOTO} className={"m-auto"} style={{maxWidth: '50px'}} />
                                    <p className="my-auto ml-2 my-agents-msg-name">Haider</p>
                                </Row>

                                <Row className="ml-auto d-flex">
                                    <p className="m-auto my-agents-msg-date">{renderDate(comment.created_time)}</p>
                                </Row>
                            </Row>
                            
                            <Row className="d-flex pl-5 pr-5" style={{backgroundColor: '#F2F2F2'}}>
                                <p className="m-auto my-agents-msg-desc">{comment.message}</p>
                            </Row>
                            
                            <Row className="d-flex px-1" style={{backgroundColor: '#F2F2F2'}}>
                                {comment.intent && comment.intent.length > 0 &&
                                comment.intent.map((intent,index) => 
                                    <Col lg={12} className="d-flex my-1" key={index}>
                                        <p className="mr-auto my-auto my-agents-msg-intent">{intent.score * 100}% Intent: {intent.intent}</p>
                                    </Col>
                                )}
                                {/* <Row className="ml-auto d-flex">
                                    <input type="checkbox" className="my-auto" />
                                </Row> */}
                            </Row>
                        </div>
                    )}
                    
                </Col>
                
                {/* BLOC 2 */}
                <Col lg={5} className="mr-auto ml-auto" style={{backgroundColor : 'white',borderRadius: '0.5rem'}}>
                    <Row className="d-flex p-4" style={{backgroundColor: 'white'}}>
                        <Row className="mr-auto d-flex">
                            <Image src={PROFILE_PHOTO} className={"m-auto"} style={{maxWidth: '50px'}} />
                            <p className="my-auto ml-2 my-agents-msg-name">Haaider</p>
                        </Row>

                        <Row className="ml-auto d-flex">
                            <BiShare color={"#E5007D"} size={"32"}  className="my-auto mr-3" />
                            <IoIosClose color={"#A4AFB7"} size={"28"} className="my-auto" />
                        </Row>
                    </Row>
                    
                    {choosenComment 
                    &&
                    <>
                    {/* BULLE GRIS */}
                    {choosenComment.message 
                    &&
                    <Row className="d-flex">
                        <Col>
                            <Row className="mt-5">
                                <Col lg={2} className="d-flex">
                                    <Image src={PROFILE_PHOTO} className={"mt-auto"} style={{maxWidth: '50px'}} />
                                </Col>

                                <Col>
                                    <div className="my-agents-msgs-bulle">
                                        <p className="my-agents-msgs-bulle-text">{choosenComment.message}</p>
                                    </div>
                                    {/* <p className="my-agents-msgs-bulle-date">1 hour ago</p> */}
                                </Col>
                            </Row>
                        </Col>

                        <Col lg={2} />
                    </Row>
                    }
                   
                   {/* BULLE BLEU */}
                   {choosenComment.manuelAnswer
                   &&
                   <Row className="d-flex">
                        <Col lg={2} />

                        <Col>
                            <Row className="mt-5">
                                <Col className="d-flex flex-column">
                                    <div className="my-agents-msgs-bulle-bleu">
                                        <p className="my-agents-msgs-bulle-text">{choosenComment.manuelAnswer}</p>
                                    </div>
                                    <p className="my-agents-msgs-bulle-date ml-auto">1 hour ago</p>
                                </Col>

                                <Col lg={2} className="d-flex">
                                    <Image src={PROFILE_PHOTO} className={"mt-auto"} style={{maxWidth: '50px'}} />
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                   }
                    </> 
                    }


                    <Row className="mt-5">
                        <Col lg={3}>
                            <Row className="justify-content-around">
                                <div className="d-flex">
                                    <Image src={BALISE_ICON} className="m-auto my-agents-msgs-tiny-icon" />    
                                </div>                                

                                <div className="d-flex">
                                    <Image src={EMO_ICON} className="m-auto my-agents-msgs-tiny-icon" />
                                </div>

                                <div className="my-agents-msgs-tiny-box d-flex">
                                    <Image src={LETTER_ICON} className="m-auto my-agents-msgs-tiny-icon" />
                                </div>
                            </Row>
                        </Col>

                        <Col lg={6} className="d-flex p-0">
                            <input 
                                className="my-auto" 
                                type="text"  
                                placeholder="Your Message..." 
                                style={{border: 0}} 
                                onKeyDown={(e) => e.key === "Enter" && answerComment(socialMediaPageSelected,choosenComment,answerComment)} 
                                onChange={(e) => setAnswerChoosenComment(e.target.value)}    
                                value={answerChoosenComment}    
                                
                            />
                        </Col>

                        <Col lg={3} />
                    </Row>

                </Col>


                {/* BLOC 3 */}
                <Col lg={3} style={{backgroundColor : 'white'}}>
                    <Row>
                    <Col lg={10}>
                    
                        <Row className="d-flex p-4" style={{backgroundColor: 'white'}}>
                            <Row className="mr-auto d-flex">
                                <Image src={PROFILE_PHOTO} className={"m-auto"} style={{maxWidth: '50px'}} />
                                <p className="my-auto ml-2 my-agents-msg-name">Enis</p>
                            </Row>

                            <Row className="ml-auto d-flex">
                                <AiOutlineEllipsis color={"#A4AFB7"} size={"32"} className="my-auto" />
                            </Row>
                        </Row>


                        <Row className="pl-3">
                            <p className="my-agents-profile-black-text">About</p>
                        </Row>

                        <Row className="pl-3 pr-3 justify-content-between">
                            <p className="my-agents-profile-gray-text">Added details</p>
                            <p className="my-agents-profile-blue-text">Edit</p>
                        </Row>

                        <Row className="pl-3 d-flex mb-3">
                            <MdPhone color={"#B4B4B4"} size={"25"} className="my-auto mr-3" />
                            <p className="my-auto my-agents-profile-black-text">+ Phone number</p>
                        </Row>
                        
                        <Row className="pl-3 d-flex mb-3">
                            <MdEmail color={"#B4B4B4"} size={"25"} className="my-auto mr-3" />
                            <p className="my-auto my-agents-profile-black-text">+ Email</p>
                        </Row>
                        
                        <Row className="pl-3 d-flex mb-3">
                            <FaBirthdayCake color={"#B4B4B4"} size={"25"} className="my-auto mr-3" />
                            <p className="my-auto my-agents-profile-black-text">16 Avril</p>
                        </Row>
                        
                        <Row className="pl-3 d-flex mb-3">
                            <FaHome color={"#B4B4B4"} size={"25"} className="my-auto mr-3" />
                            <p className="my-auto my-agents-profile-black-text">+ Adresse</p>
                        </Row>

                        <Row className="pl-3 pr-3 justify-content-between">
                            <p className="my-agents-profile-gray-text">Facebook</p>
                            <p className="my-agents-profile-blue-text">View profile</p>
                        </Row>

                        <Row className="pl-3">
                            <AiFillClockCircle color={"#B4B4B4"} size={"25"} className="mr-3" />
                            <p>Local time 16:16 PM</p>
                        </Row>

                        <Row className="pl-3">
                            <RiMapPin2Fill color={"#B4B4B4"} size={"25"} className="mr-3" />
                            <p className="my-agents-profile-blue-text">Tunis, Tunisia</p>
                        </Row>
                    </Col>

                    <Col lg={2} style={{backgroundColor: '#F9F9F9'}} />
                    </Row>
                    
                </Col>

           </Row>
            }
        </div>
    )
};

const mapStateToProps = (state) => {
    return {
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
        agentsProjects: state.agentsR.agentsProjects,
        agentsSelectedProject: state.agentsR.agentsSelectedProject,
        agentsProjectLogs: state.agentsR.agentsProjectLogs,
        agentsCommentsMatched: state.agentsR.agentsCommentsMatched,
        agentsCommentsNotMatched: state.agentsR.agentsCommentsNotMatched,
    }
};
export default connect(mapStateToProps, { getLogs, answerComment })(MyAgentsMessages);
