import React, { useEffect, useState } from 'react'
import '../Home.css';
import {
    Row,
    Col,
    Dropdown
} from 'react-bootstrap';
import Slider from "react-slick";
import Lottie from 'react-lottie';

import { connect } from 'react-redux';
import { selectSocialMediaPage, getAllPageConnected, getAllPage, getFbData, getInstaData, resetAllPages,resetFilterPages} from '../../../redux/actions/socialMediaActions';
import PageCard from './PageCard';

import { useLocation } from 'react-router-dom'; 

import animationData from '../../../assets/json/loading.json';
import { IoIosArrowDown } from 'react-icons/io';
import {ReactComponent as Preferences} from '../../../assets/images/home/preferences.svg';


function Pages(props) {

    let {
        allSocialPages,

        // Functions:
        resetAllPages,
        resetFilterPages,
        getAllPage,
        getAllPageConnected,
    } = props;

    const [loadingPages, setLoadingPages] = useState(true);
    const [allSocialPagesFiltered, setAllSocialPagesFiltered] = useState([]);
    const [filterValue, setFilterValue] = useState('all');
    const [filterRole, setFilterRole] = useState('all');
    // const [showFacebook, setShowFacebook] = useState(true);
    // const [showInstagram, setShowInstagram] = useState(true);
    const [resetCarousel, setResetCarousel] = useState(0);

    const defaultOptions = {
        loop: true,
        autoplay: true, 
        swipe: false,
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    const settings = {
        dots: true,
        infinite: false,
        speed: 700,
        slidesToShow: 4.3,
        slidesToScroll: 4,
        initialSlide: 0,
        slickGoTo: resetCarousel,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              initialSlide: 1
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
    };

    const location = useLocation();

    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a  className="pages-filter-box d-flex py-1 px-1" ref={ref} onClick={(e) => {e.preventDefault(); onClick(e);}} href="" style={{ textDecoration: 'none'}}>
            {/* <RiListSettingsFill className={"my-auto mr-2"} color={"#B4B4B4"} size={'20'} />  */}
            <Preferences className={"my-auto mr-2 ml-2"} color={"#B4B4B4"}  width="15" height="15" />
            {filterValue !== "all"
            ?
            <p className={"my-auto mr-2"}>{filterValue}</p>
            :
            <p className={"my-auto mr-2"}>Filter by page</p>
            }
            <span className={"m-auto"}>
                <IoIosArrowDown className={"my-auto ml-3"} color={"#B4B4B4"} size={'17'} /> 
            </span>
        </a>
    ));
    
    const CustomToggleRole = React.forwardRef(({ children, onClick }, ref) => (
        <a  className="pages-filter-box d-flex py-1 px-1" ref={ref} onClick={(e) => {e.preventDefault(); onClick(e);}} href="" style={{ textDecoration: 'none'}}>
            {/* <RiListSettingsFill className={"my-auto mr-2"} color={"#B4B4B4"} size={'20'} />  */}
            <Preferences className={"my-auto mr-2 ml-2"} color={"#B4B4B4"}  width="15" height="15" />
            {

            }
            <p className={"my-auto mr-2"}>Filter by role</p>
            <span className={"m-auto"}>
                <IoIosArrowDown className={"my-auto ml-3"} color={"#B4B4B4"} size={'17'} /> 
            </span>
        </a>
    ));

    const CustomPagesMenu = React.forwardRef(
        ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
        
          return (
            <div
              ref={ref}
              style={style}
              className={className}
              aria-labelledby={labeledBy}
              style={{ width: '-webkit-fill-available' }}
            >
                <Col lg={12} className="d-flex filter-item p-1" onClick={() => { setFilterValue('all'); filterPages('all',allSocialPages); setResetCarousel(0); }}>
                    <p className="ml-2 mr-auto my-auto">All</p>
                </Col>

                <Col lg={12} className="d-flex filter-item p-1" onClick={() => { setFilterValue('Facebook'); filterPages('facebook',allSocialPages); setResetCarousel(0); }}>
                    <p className="ml-2 mr-auto my-auto">Facebook</p>
                </Col>

                <Col lg={12} className="d-flex filter-item p-1" onClick={() => { setFilterValue('Instagram'); filterPages('instagram',allSocialPages); setResetCarousel(0); }}>
                    <p className="ml-2 mr-auto my-auto">Instagram</p>
                </Col>
            </div>
          );
        },
      );
    
    const CustomRoleMenu = React.forwardRef(
        ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
        
          return (
            <div
              ref={ref}
              style={style}
              className={className}
              aria-labelledby={labeledBy}
              style={{ width: '-webkit-fill-available' }}
            >
                <Col lg={12} className="d-flex filter-item p-1" onClick={() => { setFilterRole('all'); filterRoles('all',allSocialPages); setResetCarousel(0); }}>
                    <p className="ml-2 mr-auto my-auto">All</p>
                </Col>

                <Col lg={12} className="d-flex filter-item p-1" onClick={() => { setFilterRole('owner'); filterRoles('owner',allSocialPages); setResetCarousel(0); }}>
                    <p className="ml-2 mr-auto my-auto">Owner</p>
                </Col>

                <Col lg={12} className="d-flex filter-item p-1" onClick={() => { setFilterRole('invited'); filterRoles('invited',allSocialPages); setResetCarousel(0); }}>
                    <p className="ml-2 mr-auto my-auto">Invited</p>
                </Col>
            </div>
          );
        },
      );

    const filterPages = (filterBy,Pages) => {
        setLoadingPages(true);
        setFilterRole('all');
        
        if(filterBy === "all") {
            setAllSocialPagesFiltered([]);
            setTimeout(() => {
                setLoadingPages(false);
            },1500);
        } else {
            let filteredPages = Pages.filter((page) => page.platform === filterBy);
            setAllSocialPagesFiltered(filteredPages);
            setTimeout(() => {
                setLoadingPages(false);
            },1500);
        };
    };

    const filterRoles = (filterBy,Pages) => {
        setLoadingPages(true);
        setFilterValue('all');

        console.log("Filter By", filterBy, " Pages =>", Pages);
        
        let userData = JSON.parse(localStorage.getItem('userData'));

        console.log('user is =>', userData);

        // let filtredPages = Pages.filter((page) => page.admins.filter(admin => admin.idFacebook === user.idFacebook && admin.role === "owner"))
        // if(filterBy === "all") {
        //     setAllSocialPagesFiltered([]);
        //     setTimeout(() => {
        //         setLoadingPages(false);
        //     },1500);
        // } else {
        //     let filteredPages = Pages.filter((page) => page.platform === filterBy);
        //     setAllSocialPagesFiltered(filteredPages);
        //     setTimeout(() => {
        //         setLoadingPages(false);
        //     },1500);
        // };
    };
    
    useEffect(() => {
        if(location.pathname === "/home/dashboard") {
            resetFilterPages();
            getAllPage();
        } else {
            resetFilterPages();
            getAllPageConnected();
        }
    },[]);


    useEffect(() => {
        if(allSocialPages.length > 0) {
            setLoadingPages(false);
        } else {
            setLoadingPages(true);
        }
        return () => {
            // removing the listener when props.x changes
        }
    },[allSocialPages]);

    return (
    <>
        <Row>
            <Col lg={6} className="d-flex">
                {
                props.title 
                ?
                    <p className="mr-auto home-big-title" style={props.titleStyling ? props.titleStyling : {} }>{props.title}</p>
                :
                    <p className="mr-auto home-big-title">Pages Connected</p>
                }
            </Col>
            
            {allSocialPages.length > 0 
            &&
            <Col lg={4} className="d-flex p-0">
                <div className="ml-auto mt-2">
                    <Dropdown>
                        <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components"/>
                        <Dropdown.Menu as={CustomPagesMenu} />
                    </Dropdown>
                </div>
                <div className="mt-2 mr-3 ml-2">
                    <Dropdown>
                        <Dropdown.Toggle as={CustomToggleRole} id="dropdown-custom-components"/>
                        <Dropdown.Menu as={CustomRoleMenu} />
                    </Dropdown>
                </div>
            </Col>
            }
            <Col lg={1} />
        </Row>

        <div>
        {
        !loadingPages
        ?
        (
        <Row>   
        {allSocialPagesFiltered.length > 4 
        ?
        <Col lg={10} style={{height: '400px',marginTop:'13px' }}>
            <Slider {...settings}>
            {allSocialPagesFiltered.map((page) => 
                page.platform === "facebook" 
                ? 
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={null} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
                :
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"insta"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={page.imageUrl} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
            )}      
            </Slider>
        </Col>
        :
        allSocialPagesFiltered.length <= 4 && allSocialPagesFiltered.length > 0
        ?
        <Col lg={10} style={{height: '400px',marginTop:'13px' }}>
            {allSocialPagesFiltered.map((page) => 
                page.platform === "facebook" 
                ? 
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={null} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
                :
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"insta"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={page.imageUrl} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
            )}
        </Col> 
        :
        allSocialPages.length > 4
        ?
        <Col lg={10} style={{height: '400px',marginTop:'13px' }}>
            <Slider {...settings}>
            {allSocialPages.map((page) => 
                page.platform === "facebook" 
                ? 
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={null} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
                :
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"insta"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={page.imageUrl} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
            )}      
            </Slider>
        </Col>
        :
        <Col lg={10} style={{height: '400px',marginTop:'13px' }}>
            {allSocialPages.map((page) => 
                page.platform === "facebook" 
                ? 
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={null} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
                :
                <div className="p-3 mt-3 mb-5">
                    <PageCard 
                        platform={"insta"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={page.imageUrl} accessToken={page.access_token} status={page.status} team={page.user} admins={page.admins} 
                        setLoadingPages={setLoadingPages} spam={page.spam} likesComment={page.likesComment} delay={page.respReplies.statut} respReplies={page.respReplies} page={page} selectPage={props.selectSocialMediaPage}
                    />
                </div>
            )}
        </Col> 
        }
        </Row>
        )
        :
        (
        <Row className="d-flex">
            <div className="m-auto">
                <Lottie options={defaultOptions} width={200} />
            </div>
        </Row>
        )
        }
        </div>
    </>
    );
}

const mapStateToProps = (state) => {
    return {
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
        allSocialPages : state.socialMediaR.allSocialPages,
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
    }
};

export default connect(mapStateToProps, {selectSocialMediaPage,getFbData,getInstaData,getAllPageConnected,getAllPage,resetAllPages, resetFilterPages})(Pages);
