import React, { useEffect, useState } from 'react';

import  {
    Col,
    Row,
    Image,
    Form
} from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import { connect } from 'react-redux';
import { getLogs, answerComment} from '../../../redux/actions/myAgentsActions';
import moment from 'moment';
import Lottie from 'react-lottie';


import animationData from '../../../assets/json/loading.json';
import { BiSearch,BiShare } from 'react-icons/bi'; 
import { IoIosClose } from 'react-icons/io'; 
import { AiOutlineEllipsis, AiFillClockCircle } from 'react-icons/ai'; 
import { MdPhone, MdEmail } from 'react-icons/md'; 
import { FaBirthdayCake, FaHome } from 'react-icons/fa'; 
import { RiMapPin2Fill } from 'react-icons/ri'; 
import { IoIosArrowDown } from 'react-icons/io';
import {ReactComponent as Preferences} from '../../../assets/images/home/preferences.svg';
import {ReactComponent as Messenger} from '../../../assets/images/home/messenger.svg';
import {ReactComponent as Calendar} from '../../../assets/images/home/calendar.svg';
import {ReactComponent as Check} from '../../../assets/images/home/check.svg';
import {ReactComponent as LinkIcon} from '../../../assets/images/home/link.svg';

const PROFILE_PHOTO = require('../../../assets/images/home/profile-photo.png');
const LETTER_ICON = require('../../../assets/images/home/3a.png');
const EMO_ICON = require('../../../assets/images/home/emo.png');
const BALISE_ICON = require('../../../assets/images/home/balise.png');


function MyAgentsMessages(props) {

    let {
        agentsSelectedProject,
        agentsCommentsMatched,
        agentsCommentsNotMatched,
        socialMediaPageSelected,

        // Functions:
        getLogs,
        answerComment,
    } = props;

    let history = useHistory();
    const [filter,setFilter] = useState('matched');
    const [loading, setLoading] = useState(true);
    const [choosenComment, setChoosenComment] = useState(null);
    const [answerChoosenComment, setAnswerChoosenComment] = useState('');
    // const [userData, setUserData] = useState({});

    const loadingAnimation = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };
    
    useEffect(() => {
        if(agentsSelectedProject) {
            getLogs(agentsSelectedProject.post.idPost)
            .then(() => {
                setLoading(false);
            });
        }
    },[]);
    
    useEffect(() => {
        if(!agentsSelectedProject) {
            history.push('my-agents');
        } 
    },[agentsSelectedProject]);



    const renderDate = (date) => {
        let newDate = moment(date).format("DD/MM/YYYY");
        let newTime = moment(date).format("h:mm");
        return `${newDate} ${newTime}`;
    };

    return (
        <Row>
        <Col lg={3}>
                <Row style={{marginTop : '135px'}}>
                    <Col lg={12} className="p-0">
                        <p className="my-agents-msgs-title" >Liste des messages réçus</p>
                    </Col>
                    <Col lg={12}>
                            {/* <Row className="">
                                <Col lg={3}> */}
                                    <Row className="justify-content-center">
                                        <Col lg={6} className={filter === "matched" ? "my-agents-msgs-matched-btn" : "my-agents-msgs-not-matched-btn"} onClick={() => { setChoosenComment(null); setFilter('matched'); }}>
                                            Matched
                                        </Col>
                                        <Col lg={6} className={filter === "not-matched" ? "my-agents-msgs-matched-btn" : "my-agents-msgs-not-matched-btn"} onClick={() => {setChoosenComment(null); setFilter('not-matched'); }}>
                                            Not Matched
                                        </Col>
                                    </Row>
                                {/* </Col>
                            </Row> */}
                    </Col>
                    <Col lg={12}>
                            <Row className="mb-3 justify-content-end ">
                                <Col lg={6} className="">
                                    <Row className="mt-2">
                                        <Col lg={6} className="p-0">
                                            <a  className="pages-filter-box d-flex py-1 px-1 mr-1"  href="" style={{ textDecoration: 'none'}}>
                                                <Calendar className={"my-auto mr-1 ml-2"} color={"#B4B4B4"}  width="15" height="15" />
                                                <p className={"my-auto"} style={{fontSize: '13px'}}>Date</p>
                                                <span className={"m-auto"}>
                                                </span>
                                            </a>
                                        </Col>
                                        <Col lg={6} className="p-0">
