const initialState = {
    profile: {},
    tokenDetail: {}
}

const auth = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN':
            return { ...state, profile: { ...action.user.profile }, tokenDetail: { ...action.user.tokenDetail } }
        default:
            return state
    }
}
export default auth
