import React, { useState, useEffect } from 'react'

import { connect } from 'react-redux';

import Pages from '../Pages/Pages';

import {
  Col,
  Row,
  Table,
  Image,
  OverlayTrigger,
  Popover
} from 'react-bootstrap';

import { FaInfoCircle } from 'react-icons/fa';


function ManageProducts(props) {

    const [showPopover, setShowPopover] = useState(false);
  
    const handlePopover = () => {
        setShowPopover(!showPopover)
    }

    return (
      <>
        <Row className="mt-5" style={{backgroundColor: 'red'}}>
          <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Products management</p>
          <FaInfoCircle color={"#4080FF"} size={'15'} onMouseEnter={handlePopover}  />
            {/* onMouseLeave={handlePopover} */}
        {showPopover &&
        <div style={{backgroundColor: 'white', padding: '10px', color:'black', fontFamily: 'Poppins SemiBold', fontSize: '10px', maxWidth: '300px'}}>
          Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s
        </div>
        }
        </Row>

        {/* <Pages /> */}


      </>
    )
}

const mapStateToProps = (state) => {
  return {
    socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
  }
};

export default connect(mapStateToProps, { })(ManageProducts);
