import axios from 'axios'
import { host } from '../../config';



export const addProject = (post) => {
    return (dispatch) => {
        
        return axios.post(host + `/api/v1/secure/project/add`,post, {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
        .then(res => {
            console.log('project added', res.data);

            // dispatch({
            //     type: 'ADD_PROJECT',
            //     payload: res.data
            // })
        }).catch(err => {
            console.log("addProject action", err);
        })
    }
};

export const getAllIntents = (intentType) => {
    return (dispatch) => {
        
        return axios.get(host + (intentType === "generic" ? `/api/v1/intent/generiqueintent/all` : `/api/v1/intent/defaulintent/all`), {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
            .then(res => {
                console.log(`get ${intentType} intents`, res.data.data);

                // console.log(res.data.data.filter( (v,i,a)=> a.find( t =>(t.intent === v.intent) ) === v.intent ));

                let obj = [];
                let newData = {}

                for (let i = 0; i < res.data.data.length; i++) {
                    obj = {
                      [res.data.data[i]['intent']]: {
                          id: res.data.data[i]['id'],
                          name: res.data.data[i]['intent'],
                          date: res.data.data[i]['date']
                      }
                    }
                    newData = { ...newData, ...obj }
                };

                /*
                     if u want full intent object send newData :D 
                    console.log("Hey new data filtered :D", newData);
                */

                console.log("Hey new data Object keys :D", Object.keys(newData));

                dispatch({
                    type: 'GET_ALL_WIZARD_INTENTS',
                    payload: Object.keys(newData)
                })
               
            }).catch((err) => console.log("Get all intents action", err))
    }
};

export const getConnectedPagesProject = () => {
    return (dispatch) => {
        return axios.get(host + `/api/v1/secure/project/get/user`, {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
        .then(res => {
            // console.log('all projects for this user', res.data.data)
            
            dispatch({
                type: 'GET_WIZARD_ACTIVE_PAGE_POSTS',
                payload: res.data.data
            })
        }).catch((err) => console.log("err ", err))
    }
};

export const getWizardPagesPosts = (wizardPage) => {
    return (dispatch) => {

        if(wizardPage.id !== null && wizardPage.id !== undefined) {
            return axios.get(host + `/api/v1/secure/post/get/${wizardPage.id}/all`, {
                headers: {
                    'authorization': 'Bearer ' + localStorage.getItem('authtoken')
                }
            })
            .then(res => {

                // console.log("posts",res.data.data);

                dispatch({
                    type: 'GET_WIZARD_PAGE_POSTS',
                    payload: res.data.data
                })
            }).catch((err) => console.log("getWizardPagesPosts ", err))
        }
    }
};

export const setWizardSelectedPost = (post) => {
    return async (dispatch) => {

        // const selectedPage = {
        //     platform: platform,
        //     name: pageName,
        //     picture_url : picture !== null ? picture : `https://graph.facebook.com/v8.0/${idPage}/picture?access_token=${accessToken}`,
        //     id: idPage,
        //     accessToken: accessToken,
        // };

        dispatch({
            type: 'SELECT_WIZARD_POST', 
            payload: post,
        });
    }
};

export const setWizardIntentType = (intentType,navigation) => {
    return async (dispatch) => {

        dispatch({
            type: 'SELECT_WIZARD_INTENT_TYPE', 
            payload: intentType,
        });
        navigation.push('/home/auto-wizard')
    }
};

export const setWizardSelectedPage = (platform,pageName,picture,idPage,accessToken) => {
    return async (dispatch) => {

        const selectedPage = {
            platform: platform,
            name: pageName,
            picture_url : picture !== null ? picture : `https://graph.facebook.com/v8.0/${idPage}/picture?access_token=${accessToken}`,
            id: idPage,
            accessToken: accessToken,
        };


        dispatch({
            type: 'SELECT_WIZARD_PAGE', 
            payload: selectedPage,
        });
    }
};

export const nextSpecificWizardStep = (step) => {
    return async (dispatch) => {
        dispatch({ type: 'NEXT_SPECIFIC_WIZARD_STEP',payload: step });
    }
};

export const nextWizardStep = () => {
    return async (dispatch) => {
        dispatch({ type: 'NEXT_WIZARD_STEP' });
    }
};

export const prevWizardStep = () => {
    return async (dispatch) => {
        dispatch({ type: 'PREV_WIZARD_STEP' });
    }
};

export const resetWizardStep = () => {
    return async (dispatch) => {
        dispatch({ type: 'RESET_WIZARD_STEP' });
    }
};
