import React from 'react';

import { connect } from 'react-redux';


import {
    Container,
    Row,
    Col,
    Image
} from 'react-bootstrap';


const INSTA_ICON = require('../../assets/images/home/slider-instagram.svg');
const FB_ICON = require('../../assets/images/home/slider-facebook.svg');
const USER_ICON = require('../../assets/images/login/price-user.svg');
const CHECKBOX_ICON = require('../../assets/images/login/price-checkbox.svg');

function Pricing(props) {
    let { lang } = props;

    return (
        <div className="page-price">
            <Row className="justify-content-center" >
                <Col lg={12} className="d-flex ">
                    <h1 className="price-titre m-auto">Ready to try the first social media auto-moderation tool?</h1>
                </Col>
                <Col lg={12} className="d-flex">
                    <h1 className="price-sous-titre m-auto">Get your Free trial today</h1>
                </Col>
                <Col lg={10} className="d-flex">
                    <Row className="">
                        <Col lg={3} className="d-flex p-3" >
                            <Row className="" >
                                <Col lg={12} className="d-flex"><p className="price-titre-item mr-auto" >Free Trial</p></Col>
                                <Col lg={12} className="d-flex"><p className="price-sous-titre mr-auto" >SME/Artisans</p></Col>
                                <Col lg={12} className="d-flex"><p className="price-price m-auto" >â‚¬ 0/mois</p> </Col>
                                <Col lg={12} className="d-flex"><Image src={INSTA_ICON} className=" m-auto" /><Image src={FB_ICON} className=" m-auto" /></Col>
                                <Col lg={12} className="d-flex price-sous-titre-price">
                                    <Image src={USER_ICON} className=" mr-auto" />
                                    2 Social profiles, 1 User
                                </Col>
                                <Col lg={12} className="d-flex price-checkbox-titre">
                                    <Image src={CHECKBOX_ICON} className=" mr-auto" />
                                    3 active agents
                                </Col>
                                <Col lg={12} className="d-flex"><p className="price-credit-card mr-auto" >** No credit card required <br/> ** Free trial for 15 days </p> </Col>
                                <Col lg={12} className="d-flex"><button className="price-button m-auto" >Free trial</button> </Col>
                            </Row>
                        </Col>
                    </Row>
                </Col>
                <Col lg={12} className="d-flex"><p className="price-titre m-auto" > No subscription needed</p></Col>
                <Col lg={12} className="d-flex"><p className="price-credit-card m-auto text-center" > Buy one or more months<br/> Get extra reduction if you buy 3/6 or 12 months <br/>   All taxes included price  </p></Col>
            </Row>
        </div>
    )
}


const mapStateToProps = (state) => ({
    lang: state.socialMediaR.lang,
});

export default connect(mapStateToProps, {})(Pricing);
