import React from 'react';


import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';

import { IoIosClose } from 'react-icons/io';

// Icons:
const BALISE_ICON = require('../../../assets/images/home/balise.png');
const BALISE_ICON_ACTIVE = require('../../../assets/images/home/balise_active.png');
const EMO_ICON = require('../../../assets/images/home/emo.png');
const EMO_ICON_ACTIVE = require('../../../assets/images/home/emo_active.png');
const LETTER_ICON = require('../../../assets/images/home/3a.png');
const LETTER_ICON_ACTIVE = require('../../../assets/images/home/3a_active.png');



export default function WizardMiracleInput(props) {
    let {
        // Generic
        showTags,
        handleTags,
        showEmojis,
        handleEmojis,
        showKeyboard,
        handleKeyboard,
        response,
        handleResponse,

        // Specefic
    } = props;


    const renderTags = () => (
        <Row>
            <Col lg={12}>
                 <Row className="d-flex w-100">
                    <IoIosClose className="ml-auto my-auto pt-1" color={'#9F9F9F'} size={30} onClick={() => { handleTags(false) }} style={{cursor: 'pointer'}} />
                </Row>

                <Row className="d-flex w-100">
                    <h3 className="m-auto wizard-config-intent-title">Choose one tag variable</h3>
                </Row>
               
                <Row className="d-flex">
                    <Col xs={12} className="m-auto d-flex">
                        <p className="m-auto wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##FIRST_NAME##'); }}>First Name</p>
                    </Col>
                    {/* <Col xs={12} className="m-auto d-flex">
                        <p className="m-auto wizard-config-tag" onClick={() => { pushSelectedTag('##LAST_NAME##', "specific"); setShow(true); }}>Last Name</p>
                    </Col>
                    <Col xs={12} className="m-auto d-flex">
                        <p className="m-auto wizard-config-tag" onClick={() => { pushSelectedTag('##FULL_NAME##', "specific"); setShow(true); }}>Full Name</p>
                    </Col>
                    <Col xs={12} className="m-auto d-flex">
                        <p className="m-auto wizard-config-tag" onClick={() => { pushSelectedTag('##PAGE_NAME##', "specific"); setShow(true); }}>Page Name</p>
                    </Col> */}
                </Row>
          
            </Col>
        </Row>
    );

    return (
        <Row className="mx-0 px-2">

            <Col lg={12} className="mx-0">
                <label className="wizard-config-textarea-label">Don't Touuuuch</label>
            </Col>

            <Col lg={12} className="wizard-intent-miracle-box">
                
                <Row className="mb-1 mx-0 py-1">
                    <Col lg={1} className="d-flex">
                        <Image 
                            src={showTags ? BALISE_ICON_ACTIVE : BALISE_ICON} 
                            onClick={() => handleTags(!showTags)} 
                            className="m-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '19px', cursor: 'pointer'}} 
                        />    
                    </Col>                                

                    <Col lg={1} className="d-flex">
                        <Image 
                            src={showEmojis ? EMO_ICON_ACTIVE : EMO_ICON} 
                            onClick={() => handleEmojis(!showEmojis)} 
                            className="m-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '18px', cursor: 'pointer'}} 
                        />
                    </Col>
        
                    <Col lg={1} className="d-flex">
                        <Image 
                            src={showKeyboard ? LETTER_ICON_ACTIVE : LETTER_ICON} 
                            onClick={() => handleKeyboard(!showKeyboard)} 
                            className="m-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '21px', cursor: 'pointer'}} 
                        />
                    </Col>
                
                    <Col lg={8}>
                        {showTags && renderTags()}
                        {/* {showKeyBoardGeneric &&
                            <div className="wizard-config-keyboard-container">
                                <Keyboard onChange={(input) => handleArabicKeyboardChange(input,"generic")} onKeyPress={(button) => onKeyPress(button,"generic")} layout={layout} />
                            </div>
                        } */}
                        {/* {showEmojiPickerGeneric &&
                            <div className="wizard-config-emoji-container" >
                                <Picker onEmojiClick={(e,obj) => onEmojiClick(e,obj,"generic")} />
                            </div>
                        }  */}
                    </Col>
                  
                </Row>

                <Row>
                    <Col lg={12}>
                        <textarea 
                            onChange={(e) => handleResponse(e.target.value)} 
                            placeholder={"Reponse générique..."}
                            // onFocus={() => console.log("Hey I'm Focused")} 
                            value={response} 
                            className="wizard-config-textarea w-100" 
                            rows="5"
                        />
                    </Col>
                </Row>

                <Row>
                    <Col lg={12}>
                        <hr style={{height: '2px', borderColor: '#B4B4B4'}} />
                    </Col>
                </Row>

                {/*
                <Row>
                    <Col lg={12}>
                         BUTTONS QUICK SHIT REPLIES
                    </Col>
                </Row> 
                */}
            </Col>
        </Row>
    )
}
