import axios from 'axios'
import { host, trainagenthost } from '../../config';


export const getFbData = () => {
    return async (dispatch) => {
        
        let token = await localStorage.getItem('authtoken');

        return axios.get(host + '/api/v1/secure/pages/get/all/', {
            headers: {
                'authorization': 'Bearer ' + token
            }
        })
        .then(res => {
            
            console.log('pages facebook =>', res.data);

            res.data.data.map((tab,index) => console.log(tab));
            
            dispatch({
                type: 'GET_FB_DATA',
                payload: res.data
            })
        }).catch((err) => console.log("GetFbData err", JSON.stringify(err)))
    }
};

export const getInstaData = () => {
