import React from 'react';


import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';


// Icons:
const BALISE_ICON = require('../../../assets/images/home/balise.png');
const BALISE_ICON_ACTIVE = require('../../../assets/images/home/balise_active.png');

export default function WizardMiracleInput(props) {
    return (
        <Row className="pl-2">
            <Col lg={12}>
                <label className="wizard-config-textarea-label">Generic Response</label>
            </Col>

            <Col lg={12}>
                <Row className="mb-1 pl-2">
                    <div className="d-flex mr-2">
                        <Image 
                            src={props.showTags ? BALISE_ICON_ACTIVE : BALISE_ICON} 
                            onClick={() => props.handleTags(!props.showTags)} 
                            className="m-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '19px', cursor: 'pointer'}} 
                        />    
                    </div>                                

                    <div className="d-flex mr-2">
                        <Image 
                            // src={showEmojiPickerGeneric ? EMO_ICON_ACTIVE : EMO_ICON} 
                            // onClick={handleGenericEmojiPopover} 
                            className="m-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '18px', cursor: 'pointer'}} 
                        />
                    </div>
        
                    <div className="d-flex mr-2">
                        <Image 
                            // src={showKeyBoardGeneric ? LETTER_ICON_ACTIVE : LETTER_ICON} 
                            // onClick={handleGenericKeyboardPopover} 
                            className="m-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '21px', cursor: 'pointer'}} 
                        />
                        
                        {/* {showKeyBoardGeneric &&
                            <div className="wizard-config-keyboard-container">
                                <Keyboard onChange={(input) => handleArabicKeyboardChange(input,"generic")} onKeyPress={(button) => onKeyPress(button,"generic")} layout={layout} />
                            </div>
                        } */}
                        {/* {showEmojiPickerGeneric &&
                            <div className="wizard-config-emoji-container" >
                                <Picker onEmojiClick={(e,obj) => onEmojiClick(e,obj,"generic")} />
                            </div>
                        } */}
                    </div>
                </Row>
            </Col>

            {/* <Col lg={12}>
                <textarea onChange={(e) => setGenericResponse(e.target.value)} onFocus={() => console.log("Hey I'm Focused")} value={genericResponse} className="wizard-config-textarea" rows="5"/>
            </Col> */}
        </Row>
    )
}
