const initialState = []

const allPages = (state = initialState, action) => {
    switch (action.type) {
        case 'GETALLPAGES':
            return action.pages.data.data
        case 'UPDATEPAGE':
            let updatedState = state.filter(el => el.id !== action.page.data.data.id)
            return [...updatedState, action.page.data.data]
        default:
            return state
    }
}
export default allPages
