import React, { useEffect } from 'react'
import '../Home.css';

import PageCard from './PageCard';
import {
    Row,
    Col,
    Image,
} from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import Lottie from 'react-lottie';

import { connect } from 'react-redux';
import { getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import animationData from '../../../assets/json/loading.json';



function Pages(props) {


    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };

    useEffect(() => {
        // Getting insta & fb DATA :
        setTimeout(() => {
            props.getFbData();
        },1500);
        
        props.getInstaData();

    },[]);

    // <img src={'https://graph.facebook.com/v3.2/' + props.page.idPage + '/picture?access_token=' + props.page.access_token} alt="page thumbnail" style={{ borderRadius: 50 + "%" }} />
      
    return (
        <>
        <Row>
            <p className="home-big-title">Pages Connected</p>
        </Row>

        <Row>
            {
            props.fbData
            ?
                (
                    <>
                    <OwlCarousel margin={10} items={4}>
                        {Object.keys(props.fbData).map( (key, index) =>
                            props.fbData[key].map((page,index) => (
                                <PageCard platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} accessToken={page.access_token} />
                            ))
                        )}
                    </OwlCarousel>
                    </>
                )
            :
                (
                <div className="d-flex" style={{backgroundColor: 'red', width: '100%'}}>
                    <div className="m-auto">
                        <Lottie options={defaultOptions} width={400} /*height={400}*/ />
                    </div>
                </div>
                )
            }
            
        </Row>
        </>
    );
}


const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
    }
};

export default connect(mapStateToProps, { getFbData, getInstaData })(Pages);
