import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux';
import { addProductInCategory, updateCategoryName, reDisptachPage } from '../../../redux/actions/myProductsActions';

import ProductDetails from './ProductDetails';

import {
    Col,
    Row,
    Image,
    Modal,
    // Spinner
} from 'react-bootstrap';
import { withRouter } from 'react-router-dom';

import Swal from 'sweetalert2/dist/sweetalert2.js'

import { FaInfoCircle } from 'react-icons/fa';
import { MdModeEdit } from 'react-icons/md';

const INSTA_ICON = require('../../../assets/images/home/insta-icon.png');
const FB_ICON = require('../../../assets/images/home/fb-icon.png');

export const UpdateProducts = (props) => {
    
    let { 
        history,
        socialMediaPageSelected,
        categorySelected,
        addProductInCategory,
        updateCategoryName,

        // Fcts:
        reDisptachPage
    } = props;

    const [showPopover, setShowPopover]  = useState(false);
    const [categoryName,setCategoryName] = useState('');
    const [categoryDone,setCategoryDone] = useState(true);
    const [newProduct,setNewProduct] = useState('');
    const [allProducts, setAllProducts] = useState([]);

    const handlePopover = (delay) => {
        setTimeout( () => {
          setShowPopover(!showPopover);
        },delay)
    };
    

    const renderPopover = () => (
        <div className="d-flex manage-products-popover">
          <p className="m-auto">
            Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s
          </p>
        </div>
    );

    const goToUpdateCategoryName = (e) => {
        if(e.key === 'Enter') {
            updateCategoryName(categorySelected,categoryName,socialMediaPageSelected)
            .then(() => {
                setCategoryDone(true)
            })
            .catch(() => {
                Swal.fire({
                    position: 'center',
                    icon: 'error',
                    title: 'Error while updating category name',
                    showConfirmButton: false,
                    timer: 2000
                })
            })
        }
    };

    const backToMyProducts = () => {
        reDisptachPage(socialMediaPageSelected,history);
    };

    
    useEffect(() => {
       if(!socialMediaPageSelected) {
           history.push('manage-products');
       } else {
           setCategoryName(categorySelected.name);
       };
    }, []);

    return (
    <div style={{height: window.innerHeight * 1.2,marginTop: '86px'}} className="d-flex flex-column">
           
        <Row className="mt-5 mr-auto">
        <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Update category</p> 
        <FaInfoCircle style={{transitionDuration: '300ms' }} color={!showPopover ? "#4080FF" : "#E5007D"} size={'15'} onMouseEnter={() => handlePopover('20')} onMouseLeave={() => handlePopover('300')} />
        { showPopover && renderPopover() }
        </Row>

        {/* SELECTED PAGE */}
        {socialMediaPageSelected &&
        <Row className="mr-auto d-flex mb-3">
            <Row className="wizard-selected-page-container m-auto" style={{backgroundColor: 'transparent'}}>
                <div className="d-flex flex-column mr-3">
                    <Image src={socialMediaPageSelected.picture_url} className="m-auto" style={{ maxWidth: '40px' }} />
                    <Image src={socialMediaPageSelected.platform === "instagram" ? INSTA_ICON : FB_ICON} className="ml-auto" style={{zIndex: 1, marginTop: '-10px'}}/>
                </div>
                <p className="m-auto wizard-pages-page-title">{socialMediaPageSelected.name}</p> 
            </Row>
        </Row>
        }
        
        {/* Category Name */}
        <Row className="d-flex">   
            {categoryName && categoryDone
            ?
            (
            <Col lg={5} className="p-0">
                <div className="d-flex">
                    <p className="my-auto wizard-pages-page-title">{categoryName}</p>
                    <MdModeEdit className="my-auto ml-3" color={'#4B4F56'} size={'20'} onClick={() => setCategoryDone(false)} style={{ cursor: 'pointer' }} />
                </div>
            </Col>
            )
            :
            (
            <Col lg={2} className="add-new-product-input-container">
                <input 
                    type="text" className="add-new-product-input w-100" placeholder="Type category name"  
                    value={categoryName} onChange={(e) => setCategoryName(e.target.value)} onKeyDown={(e) => goToUpdateCategoryName(e)}
                />
            </Col>
            )
            }
            <Col lg={10} />
        </Row>
        
        {/* ADD PRODUCTS */}
        <Row className="d-flex mt-3">   
            <Col lg={3}>
                <Row className="justify-content-between">
                    <Col lg={6} className="add-new-product-input-container">
                        <input 
                            type="text" 
                            className="add-new-product-input w-100" 
                            disabled={categoryDone ? false : true} value={newProduct} 
                            onChange={(e) => setNewProduct(e.target.value)} 
                            onKeyDown={(e) => { if(e.key === 'Enter') setNewProduct(e.target.value) }}
                        />
                    </Col>

                    <Col lg={5}>
                        <Row 
                            className={categoryDone ? "team-invite-btn d-flex" : "manage-products-add-disabled-btn d-flex"} 
                            // OLD => onClick={ () => categoryDone ? createProducts() : null }
                            onClick={ () => {addProductInCategory(categorySelected,socialMediaPageSelected,newProduct); setNewProduct("");}} 
                        >
                            {/* <MdAddCircleOutline className="my-auto mr-1" color={iconColor} size={'20'} /> */}
                            <p className="m-auto">Add products</p>
                        </Row>
                    </Col>
                </Row>
            </Col>

            <Col lg={7} />
        </Row>
        
        {categorySelected &&
        <>
        <Row className="mt-5 mr-0">
            <Col lg={10} className="p-3" style={{backgroundColor: 'white', borderRadius: '4px'}}> 
                {
                categorySelected.children.length > 0
                &&
                <>
                {/* TITLES */}
                <Row className="px-1">
                    <Col lg={5} className="d-flex py-3">
                        <div className="mr-auto d-flex">
                            <p className="my-auto manage-products-table-title">Products</p>
                        </div>
                    </Col>

                    <Col lg={7} className="py-3">
                        <div className="d-flex mr-auto">
                            <p className="mr-5 my-auto manage-products-table-title">Synonyms</p>
                        </div>
                    </Col>
                    <hr  style={{borderColor: '#EBEDF0', opacity: '1',padding: '0',margin: 'auto',width: '96%'}} />
                </Row>

                {/* LINE */}
                {categorySelected.children.length > 0 && 
                categorySelected.children.map((product,index) => 
                <Row className="pl-2 pr-4 ">
                    <Col lg={12} className="p-0">
                        <ProductDetails  
                            product={product} 
                            synonyms={product.synonyms} 
                            indexProduct={index} 
                            allProducts={allProducts} setAllProducts={setAllProducts} 
                        />
                    </Col>
                        <hr  style={{borderColor: '#EBEDF0', opacity: '1',padding: '0',margin: 'auto',width: '96%'}} />
                </Row>
                )}
                </>
                }
            </Col>

            <Col lg={2} />
        </Row>

        {/* Buttons */}
        <Row className="mt-5 mr-0">
            <Col lg={10} className=""> 
                <Row className="justify-content-between">
                    <div className="wizard-pages-inactive-btn py-2 px-4"  onClick={() => backToMyProducts()}>
                        Back    
                    </div>
                    {/* <div className="wizard-pages-active-btn" onClick={() => prepareEntitiesAndSave() }>
                        { btnLoading ? <Spinner animation="border" variant="light" /> : "Save"}
                    </div> */}
                </Row>
            </Col>

            <Col lg={2} />
        </Row>

        </>
        }
    </div>
    );
}

const mapStateToProps = (state) => {
    return {
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
        categories : state.productsR.categories,
        categorySelected : state.productsR.categorySelected,
    }
};

