import React, { useState, useRef } from 'react';

import {
    Row,
    Col,
    Popover,
    Overlay,
    Modal,
    Button,
    Form,
} from 'react-bootstrap';
import Popup from 'reactjs-popup';
import axios from 'axios';
import ReactTooltip from 'react-tooltip';


import { connect } from 'react-redux';
import { addGenericResponse, updateGenericResponse } from '../../../redux/actions/wizardActions';
import { host } from '../../../config';

import { FaTrashAlt } from 'react-icons/fa';
import { IoIosArrowDown,IoIosClose } from 'react-icons/io';
import { AiOutlineCloseCircle } from 'react-icons/ai';
import { MdAddCircleOutline } from 'react-icons/md';


function WizardIntentDetails(props) {

    const [intentState, setIntentState] = useState('save');
    const [products, setProducts] = useState([]);

    const [genericResponse, setGenericResponse] = useState('');
    const [specificResponse, SetSpecificResponse] = useState('');
    const [productName, setProductName] = useState('');

    const [show, setShow] = useState(false);
    const [showModal, setShowModal] = useState(true);
    const [showModalInput, setShowModalInput] = useState(false);
    const [target, setTarget] = useState(null);
    const ref = useRef(null);

    const handleModal = () => {
        setShow(false);
        setShowModal(!showModal);
    };

    const handleClick = (event) => {
        setShow(!show);
        setTarget(event.target);
    };

    const handleSaveGenericResponse = (intent,wizardIdProject) => {
        let project = {
            genericAnswer : genericResponse,
            intent: intent,
        };
        
        if(intentState === 'save') {
            props.addGenericResponse(project,wizardIdProject);
            setIntentState('update');
        } else {
            props.updateGenericResponse(project,wizardIdProject);
        }
    };
    
    const addNewProduct = (idProjet) => {
        
        // First update api : entity === categorie :
        let productToSend = {
            idPage: props.wizardSelectedPage.id,
            typePage : props.wizardSelectedPage.platform,
            entity: productName,
            intent: props.intent,
            genericAnswer: genericResponse,
            specificAnswer: specificResponse,
            categorie: productName,
        };

        setProducts(products => [...products, productToSend ]);
        // Reset
        setShow(!show);
        setProductName('');
        SetSpecificResponse('');

        // axios.put(host + `/api/v1/secure/project/firstupdate/${idProjet}`, productToSend, {
        //     headers: {
        //         'authorization': 'Bearer ' + localStorage.getItem('authtoken')
        //     }
        // })
        // .then( res => {
        //     console.log('first update response redux =>', res.data)   
        //     // if (res.data.data === "not exist") {
        //     //     dispatch(showCategoryPoppup())
        //     // } else {
        //     //     dispatch(hideCategoryPoppup())
        //     // }
        // })
        // .catch( err => console.log("Wizard Intent Detail err", err))
    };

    const deleteProduct = (index) => {
        let oldProducts = [...products];

        oldProducts.splice(index,1);
        setProducts(oldProducts);    
    };

    return (
                  
        <Col lg={5} className="mb-3 mr-3" key={props.intent.id}>
            
            <div className="d-flex flex-column wizard-config-intents-container" style={{borderRadius: '0.5rem'}}>
                
                <div className="justify-content-between d-flex flex-row mx-0 p-0  wizard-config-intents-top-row">
                    <p className="my-auto wizard-config-intent-title p-2">{props.intent}</p>

                    <div className="my-auto d-flex p-2">
                        <FaTrashAlt  color={"#818E94"} size={"20"} className="m-auto" style={{cursor: 'pointer'}} onClick={ () => props.stateManagment(props.selectedIntents.filter( intent =>(intent !== props.intent ))) } />
                        <IoIosArrowDown color={"#818E94"} size={"20"} className="my-auto ml-2" style={{cursor: 'pointer'}} />
                    </div>
                </div>
                
              
                <div>
                    <div className="mr-2 ml-2 mt-2 mb-2">
                        <label className="wizard-config-textarea-label">Generic Response</label>
                        <textarea onChange={(e) => setGenericResponse(e.target.value)} value={genericResponse} className="wizard-config-textarea" rows="5"/>
                    </div>

                    <div className="d-flex">
                        {
                            intentState === 'save' 
                            ?
                                (<div className="ml-auto mr-2 mb-2 wizard-config-update-btn" onClick={() => handleSaveGenericResponse(props.intent,props.wizardIdProject)}>Save</div>)
                            :
                                (<div className="ml-auto mr-2 mb-2 wizard-config-update-btn" onClick={() => handleSaveGenericResponse(props.intent,props.wizardIdProject)}>Update</div>)
                        }
                    </div>

                    {/* PRODUCTS */}
                    {
                        intentState === 'update' &&
                        <Col lg={11} className="mx-auto mt-4">
                            <Row className="justify-content-between">
                                {
                                    products.length > 0 &&
                                    products.map((product,index) => 
                                        <Col key={index} lg={5} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-product-box">
                                            {
                                                product.entity.length > 8
                                                ?
                                                <p data-for='entityTip' data-tip={product.entity} className="m-auto wizard-config-product-name">{product.entity.substring(0,7)}...</p>
                                                :
                                                <p className="m-auto wizard-config-product-name">{product.entity}</p>
                                            }
                                            <AiOutlineCloseCircle className="my-auto ml-1" color={"#F49BCC"} size={"20"} style={{cursor: 'pointer'}} onClick={() => deleteProduct(index)} />
                                            <ReactTooltip id='entityTip' textColor='#fff' backgroundColor='#E5007D' />
                                        </Col> 
                                    )
                                }    
                                
                                <Col lg={5} onClick={handleClick} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-add-product-box">
                                    <MdAddCircleOutline className="my-auto mr-2" color={"#4080FC"} size={"20"} style={{cursor: 'pointer'}} />
                                    <p className="my-auto wizard-config-add-product-name">Add product</p>
                                </Col>

                                <div ref={ref}>
                                    <Overlay
                                        show={show}
                                        target={target}
                                        placement="right"
                                        container={ref.current}
                                        containerPadding={50}
                                    >
                                        <Popover id="popover-contained">
                                            <Popover.Content>
                                                <div className="d-flex">
                                                    <IoIosClose className="ml-auto" color={'#9F9F9F'} size={'25'} onClick={handleClick} style={{cursor: 'pointer'}} />
                                                </div>
                                                <div className="mr-2 ml-2 mt-2 mb-2">
                                                    <label className="wizard-config-textarea-label">Product name</label>
                                                    <input onChange={(e) => setProductName(e.target.value)} value={productName} className="wizard-config-textarea" />
                                                </div>
                                                
                                                <div className="mr-2 ml-2 mt-2 mb-2">
                                                    <label className="wizard-config-textarea-label">Specific response</label>
                                                    <textarea onChange={(e) => SetSpecificResponse(e.target.value)} value={specificResponse} className="wizard-config-textarea" rows="3"/>
                                                </div>

                                                <div className="mr-2 ml-2 mt-2 mb-2 d-flex">
                                                   <div className="ml-auto mb-2 wizard-config-update-btn" onClick={() => addNewProduct(props.wizardIdProject)}>Add</div>
                                                   <div className="ml-auto mb-2 wizard-config-update-btn" onClick={handleModal}>Modal</div>
                                                </div>
                                            </Popover.Content>
                                        </Popover>
                                    </Overlay>
                                </div>

                                {/* <Popup trigger={<button className="button"> Open Modal </button>} modal>
                                    <span> Modal content </span>
                                </Popup> */}
                            </Row>
                        </Col>
                    }
                </div>
               
            
            </div>

            <Modal show={showModal} onHide={handleModal} centered>
                <Modal.Header closeButton />

                <Modal.Body>
                    
                    <Row className="d-flex mb-3">
                        <p className="m-auto wizard-config-intent-title">Please specify the category</p>
                    </Row>

                    <Row className="mb-3">
                        <Col lg={1} />

                        <Col lg={10} className="d-flex justify-content-between p-0 mx-0">
                            <Col lg={8} className="d-flex">
                                {showModalInput && <input type="text" className="mr-auto w-100 my-auto add-new-category-input-container" />}
                            </Col>

                            <Col lg={4} className="d-flex">
                                <p 
                                    className="ml-auto my-auto wizard-config-intent-name" 
                                    style={{cursor: 'pointer'}} 
                                    onClick={() => setShowModalInput(true)}
                                >
                                create new
                                </p>
                            </Col>
                        </Col>

                        <Col lg={1} />
                    </Row>

                    <Row>
                        <Col lg={1} />

                        <Col lg={10}>
                            <Form.Group className="d-flex w-100">
                                <Form.Control disabled={!showModalInput} as="select" className="wizard-config-intent-title" custom>
                                    <option className="wizard-config-intent-title">Choose Category...</option>
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </Form.Control>
                            </Form.Group>
                        </Col>

                        <Col lg={1} /> 
                    </Row>

                </Modal.Body>
                
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
       
        </Col>
    )
}

const mapStateToProps = (state) => ({
    wizardIdProject: state.wizardR.wizardIdProject,
    wizardIntentType: state.wizardR.wizardIntentType,
    wizardSelectedPage: state.wizardR.wizardSelectedPage,
    // wizardStep: state.wizardR.wizardStep,
})

export default connect(mapStateToProps, { addGenericResponse, updateGenericResponse })(WizardIntentDetails)
