                        {/* FOR HAIDER */}
                        {/* MATCHED */}
                        {filter === "matched" 
                        &&
                            <Row  
                                className="pt-3 pb-5 mt-3 justify-content-center" 
                                style={{cursor: 'pointer',backgroundColor: '#F2F2F2'}}
