                                <Col lg={12} className="" style={{backgroundColor: '#F2F2F2'}}>
                                    <Row>
                                        <Col lg={6} className="d-flex p-0">
                                            <Image src={PROFILE_PHOTO} className={"m-auto"} style={{maxWidth: '35px'}} />
