import React from 'react';


import {
    Row,
    Col,
    Image,
    OverlayTrigger,
    Popover,
    Form,
} from 'react-bootstrap';
import Picker from 'emoji-picker-react';
// Arabic keyboard: 
import Keyboard from 'react-simple-keyboard';
import layout from "simple-keyboard-layouts/build/layouts/arabic";

import { IoIosClose } from 'react-icons/io';

// Icons:
const BALISE_ICON = require('../../../assets/images/home/balise.png');
const BALISE_ICON_ACTIVE = require('../../../assets/images/home/balise_active.png');
const EMO_ICON = require('../../../assets/images/home/emo.png');
const EMO_ICON_ACTIVE = require('../../../assets/images/home/emo_active.png');
const LETTER_ICON = require('../../../assets/images/home/3a.png');
const LETTER_ICON_ACTIVE = require('../../../assets/images/home/3a_active.png');



export default function WizardMiracleInput(props) {
    let {
        // Generic
        showTags,
        handleTags,
        showEmojis,
        handleEmojis,
        showKeyboard,
        handleKeyboard,
        response,
        handleResponse,

        quickReplyType,
        handleQuickReplyType,

        quickReplyTitle,
        handleQuickReplyTitle,

        quickReplyValue,
        handleQuickReplyValue,
        
        quickReplies,
        handleQuickReplies,



        // Specefic
    } = props;


    const Tags = (
        <Popover id="popover-tags">
          <Popover.Content>
            <Row>
                <Col lg={12}>
                    {/* <Row className="d-flex w-100">
                        <IoIosClose className="ml-auto my-auto pt-1" color={'#9F9F9F'} size={30} onClick={() => { handleTags(false) }} style={{cursor: 'pointer'}} />
                    </Row> */}

                    <Row className="d-flex pl-5">
                        <Col xs={12} className="d-flex">
                            <p className="my-auto mr-auto my-2 wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##FIRST_NAME##'); }}>First Name</p>
                        </Col>
                         
                        <Col xs={12} className="d-flex">
                            <p className="my-auto mr-auto my-2 wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##LAST_NAME##'); }}>Last Name</p>
                        </Col>

                        <Col xs={12} className="d-flex">
                            <p className="my-auto mr-auto my-2 wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##FULL_NAME##'); }}  >Full Name</p>
                        </Col>

                        <Col xs={12} className="m-auto d-flex">
                            <p className="my-auto mr-auto my-2 wizard-config-tag" onClick={() => { handleResponse(prevResponse => prevResponse + '##PAGE_NAME##'); }}  >Page Name</p>
                        </Col> 
                    </Row>
                </Col>
            </Row>
          </Popover.Content>
        </Popover>
    );

     const quickRepliesPopover = (
        <Popover id="popover-quick-replies">
          <Popover.Content>
            <Row className="justify-content-center">
                <Col md={12}>
                    <Row>
                        <Col md={12} className="mt-2 mb-3">
                            <Form.Control as="select" onChange={(e) => handleQuickReplyType(e.target.value)} defaultValue="url">
                                <option value="url">Url</option>
                                <option value="call">Call</option>
                            </Form.Control>
                        </Col>
                        
                        <Col md={12} className="mt-2">
                            <Row className="d-flex">
                                <Col md={12}>
                                    <Form.Control 
                                        type={"text"} 
                                        onChange={(e) => handleQuickReplyTitle(e.target.value)} 
                                        value={quickReplyTitle}  
                                        placeholder="Title" 
                                    />
                                </Col>

                                <Col md={12} className="d-flex">
                                    <p className="ml-auto wizard-config-input-feedback">Remaining characters: 20</p>
                                </Col>
                            </Row>
                        </Col>

                        <Col md={12} className="mb-2">
                            <Form.Control 
                                type={quickReplyType === "url" ? "text" : "number"} 
                                onChange={(e) => handleQuickReplyValue(e.target.value)} 
                                value={quickReplyValue} 
                                placeholder={quickReplyType === "url" ? "www.example.com" : "(+216) 99 999 999"} 
                            />
                        </Col>
                    </Row>
                </Col>

                <Col md={11} className="d-flex pl-1">
                    {/* <div className="wizard-pages-active-btn py-3 px-5" onClick={() => addNewQuickReply()}>
                        OK
                    </div> */}
                    <div className="wizard-pages-active-btn py-3 px-5">
                        OK
                    </div>
                </Col>   
            </Row>
                
          </Popover.Content>
        </Popover>
     );

    return (
        <Row className="mx-0 px-2">

            <Col lg={12} className="mx-0 px-0">
                <label className="wizard-config-textarea-label">Don't Touuuuch</label>
            </Col>

            <Col lg={12} className="wizard-intent-miracle-box px-1">
            
                <Row className="mb-1 mx-0 pt-1 px-2">
                    <Col lg={1} className="d-flex p-0">
                        <OverlayTrigger trigger="click" placement="top" overlay={Tags}>
                            <Image 
                                src={showTags ? BALISE_ICON_ACTIVE : BALISE_ICON} 
                                onClick={() => handleTags(!showTags)} 
                                className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                                style={{ width: '19px', cursor: 'pointer'}} 
                            />   
                        </OverlayTrigger> 
                    </Col>                                

                    <Col lg={1} className="d-flex p-0">
                        <Image 
                            src={showEmojis ? EMO_ICON_ACTIVE : EMO_ICON} 
                            onClick={() => handleEmojis(!showEmojis)} 
                            className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '18px', cursor: 'pointer'}} 
                        />
                    </Col>
        
                    <Col lg={1} className="d-flex p-0">
                        <Image 
                            src={showKeyboard ? LETTER_ICON_ACTIVE : LETTER_ICON} 
                            onClick={() => handleKeyboard(!showKeyboard)} 
                            className="my-auto mr-auto my-agents-msgs-tiny-icon" 
                            style={{ width: '21px', cursor: 'pointer'}} 
                        />
                    </Col>
                
                    <Col lg={8}>
                        {showKeyboard &&
                            <div className="wizard-config-keyboard-container">
                                <Keyboard 
                                    onChange={(input) => handleResponse(prevResponse => prevResponse + input.charAt(input.length - 1))} 
                                    // onKeyPress={(button) => onKeyPress(button,"generic")} 
                                    layout={layout} 
                                />
                            </div>
                        }

                        {showEmojis &&
                            <div className="wizard-config-emoji-container" >
                                <Picker onEmojiClick={(e,obj) => handleResponse(prevResponse=> prevResponse + obj.emoji) } />
                            </div>
                        } 
                    </Col>
                </Row>

                <Row>
                    <Col lg={12} className="mx-0">
                        <textarea 
                            onChange={(e) => handleResponse(e.target.value)} 
                            placeholder={"Your Message..."}
                            // onFocus={() => console.log("Hey I'm Focused")} 
                            value={response} 
                            className="wizard-config-textarea w-100 py-0" 
                            rows="5"
                        />
                    </Col>
                </Row>

                <Row >
                    <Col lg={12} className="my-0 py-0">
                        <hr style={{height: '2px', borderColor: '#B4B4B4', margin: '1px'}} />
                    </Col>
                </Row>

                
                <Row className="mx-0">
                    <Col lg={12}>
                        <Row>
                            <OverlayTrigger trigger="click" placement="top" overlay={quickRepliesPopover}>
                                <Col lg={4} className="wizard-config-quick-replies-btn my-1 py-1 d-flex">
                                    <p className="m-auto">Add button</p>
                                </Col>
                            </OverlayTrigger>
                        </Row>
                    </Col>
                </Row> 
               
            </Col>
        </Row>
    )
}
