import React, { useState, useRef,useEffect } from 'react';
import { connect } from 'react-redux';

import AddSynonymProducts from './AddSynonymProducts';
import { FaTrashAlt } from 'react-icons/fa';
import { FiEdit } from 'react-icons/fi';
import { AiOutlineCloseCircle } from 'react-icons/ai';

import {
    Col,
    Row,
} from 'react-bootstrap';

export const ProductDetails = (props) => {

    const inputRef = useRef();
    const [modifyProductContent, setModifyProductContent] = useState(false);
    const [synonymBoxBorder, setSynonymBoxBorder] = useState(false);


    let { product,index,addSynonym } = props;

    useEffect(() => {
        modifyProductContent && inputRef.current.focus();
    },[modifyProductContent]);


    return (
    <Row className="p-3" key={index} style={{ borderBottom: '1px solid #EBEDF0' }}>
        <Col lg={5} className="d-flex">
            <div className="mr-auto d-flex">
                {modifyProductContent
                ?
                <input type="text" className="my-auto synonym-box-white w-100" style={{ border: '1px solid #818E94' }} />
                :
                <p className="my-auto manage-product-entity-name">{product.content}</p>
                }
            </div>
        </Col>

        <Col lg={7}>
            <Row>
                <Col lg={10}>
                    <Row className="mx-0">
                        {product.synonyms.length > 0
                        &&
                        product.synonyms.map((synonym,index) => 
                            <Col key={index} lg={4} className="synonym-box-gray d-flex mr-1 mb-2">
                                <Row className="d-flex mx-auto">
                                    <p className="my-auto">{synonym}</p>
                                    <AiOutlineCloseCircle className="my-auto ml-1" color={'#818E94'} size={'17'} style={{ cursor: 'pointer' }} />
                                </Row>
                            </Col>    
                        )}

                        <Col lg={4} className="synonym-box-white d-flex mr-1 mb-2" style={{border: synonymBoxBorder ? '1px solid #818E94' : '1px dashed #818E94'}} onClick={() => setSynonymBoxBorder(true)}>
                            <AddSynonymProducts addSynonym={addSynonym} index={index} product={product} setSynonymBoxBorder={setSynonymBoxBorder} />
                        </Col>
                    </Row>
                </Col>

                <Col lg={2} className="d-flex">
                    <Row className="ml-auto">
                        <FiEdit className="my-auto" color={'#E5007D'} size={'25'} style={{ cursor: 'pointer' }} onClick={ () => setModifyProductContent(true) }/>
                        <FaTrashAlt className="my-auto ml-3" color={'#E5007D'} size={'25'} style={{ cursor: 'pointer' }} />
                    </Row>
                </Col>
            </Row>
        </Col>
    </Row> 
    );
};

const mapStateToProps = (state) => ({
    
});

export default connect(mapStateToProps, {})(ProductDetails)
