import React, { useEffect } from 'react';
import Pages from '../Pages/Pages';


import { connect } from 'react-redux';                       
import { getDashboard } from '../../../redux/actions/socialMediaActions';

import {
    Col,
    Row,
} from 'react-bootstrap';

import { PieChart } from 'react-minimal-pie-chart';

function Dashboard(props) {
    
    let {
        socialMediaPageSelected,
        dashboardData,

        // FUNCTIONS
        getDashboard,
    } = props;

    useEffect(() => {
        if(socialMediaPageSelected) {
            getDashboard(socialMediaPageSelected);
        }
        return () => {

        };
    }, [socialMediaPageSelected]);

    return (
        <div style={{ height: window.innerHeight * 2, marginTop: '135px' }}>
            
            <Pages />

            {socialMediaPageSelected 
            &&
            <>
            <Row>
                <p className="home-big-title" style={{ fontSize: '20px', fontFamily: 'Poppins SemiBold' }}>Most requested intents</p>
            </Row>
            
            <Row>
                <Col lg={10} className="p-4" style={{background: 'white'}}>
                    <Row>
                        <Col lg={8}>
                            <Row className="justify-content-between mx-2">
                                <p>Intents</p>
                                <p>Total</p>
                            </Row>

                            {/* LINE */}
                            {dashboardData
                            ?
                            dashboardData.map((data,i) => 
                                <>
                                <Row key={i} className="justify-content-between mx-2">
                                    {/* <span className="intent-dashboard d-flex" style={{background: "#"+((1<<24)*Math.random()|0).toString(16).slice(2,8) }}>  */}
                                    <span className="intent-dashboard d-flex" style={{background: data.color}}> 
                                        <p className="m-auto px-4">{data.title}</p>
                                    </span>

                                    <span className="d-flex">
                                        <p className="intent-dashboard-total m-auto">{data.value}</p>
                                    </span>
                                </Row>
                                <hr className="intent-dashboard-separator" />
                                </>
                            )
                            :
                            <p>NO DATA</p>
                            }
                        </Col>

                        <Col lg={4} className="d-flex">
                            <div className="m-auto">
                                <PieChart
                                lineWidth={15}
                                    data={[
                                        { title: 'One', value: 10, color: '#E38627' },
                                        { title: 'Two', value: 15, color: '#C13C37' },
                                        { title: 'Three', value: 20, color: '#6A2135' },
                                    ]}
                                />;
                            </div>
                        </Col>

                    </Row>
                </Col>
            </Row>

            </>
            }

        </div>
    )
}


const mapStateToProps = (state) => {
    return {
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
        dashboardData : state.socialMediaR.dashboardData,
    }
};

export default connect(mapStateToProps, { getDashboard, })(Dashboard);
