import React, { useEffect } from 'react'
import './AutoWizard.css';

import {
    Row,
    Col
} from 'react-bootstrap';


import WizardSteps from './WizardSteps';
import WizardPages from './WizardPages';
import WizardPosts from './WizardPosts';

// Redux:
import { connect } from 'react-redux';
import { nextWizardStep } from '../../../redux/actions/wizardActions';

function AutoWizard(props) {

    useEffect(() => {
        console.log("My current wizard step", props.wizardStep);
    }, []);

    useEffect(() => {
        console.log("New Value", props.wizardStep);
    }, [props.wizardStep])

    return (
        <Col style={{height: window.innerHeight}} className="d-flex flex-column" >
            
            <Row className="mx-auto">
                <p className="m-auto" className="wizard-title">iModo Wizard</p>
            </Row>

            <WizardSteps />

            <WizardPages />

            <div onClick={() => props.nextWizardStep()}>CLICK</div>
            {/* <WizardPosts /> */}

        </Col>
    )
}

const mapStateToProps = (state) => {
    return {
        wizardStep: state.wizardR.wizardStep,
    }
};

export default connect(mapStateToProps, { nextWizardStep })(AutoWizard);
