import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux';



import {
    Col,
    Row,
    Image
} from 'react-bootstrap';

import {
    withRouter
  } from 'react-router-dom';

import { FaInfoCircle,FaTrashAlt } from 'react-icons/fa';
import { FiEdit } from 'react-icons/fi';
const INSTA_ICON = require('../../../assets/images/home/insta-icon.png');
const FB_ICON = require('../../../assets/images/home/fb-icon.png');


export const AddNewProducts = (props) => {

    const [showPopover, setShowPopover] = useState(false);

    const { history } = props;

    const handlePopover = (delay) => {
        setTimeout( () => {
          setShowPopover(!showPopover);
        },delay)
    };

    const renderPopover = () => (
        <div className="d-flex manage-products-popover">
          <p className="m-auto">
            Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s
          </p>
        </div>
    );

    useEffect(() => {
       if(!props.socialMediaPageSelected) {
           history.push('manage-products');
       }
    }, [])

    useEffect(() => {
       console.log('here', props.socialMediaPageSelected);
    }, [props.socialMediaPageSelected])

    return (
        <div style={{height: window.innerHeight * 1.2}} className="d-flex flex-column">
           
           <Row className="mt-5 mr-auto">
                <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Add category</p>
                <FaInfoCircle color={"#4080FF"} size={'15'} onMouseEnter={() => handlePopover('20')} onMouseLeave={() => handlePopover('300')} />
                { showPopover && renderPopover() }
            </Row>

            {/* SELECTED PAGE */}
            {
            props.socialMediaPageSelected &&
            <Row className="mr-auto d-flex mb-3">
                <Row className="wizard-selected-page-container m-auto p-3" style={{backgroundColor: 'transparent'}}>
                    <div className="d-flex flex-column mr-3">
                        <Image src={props.socialMediaPageSelected.picture_url} className="m-auto" style={{ maxWidth: '40px' }} />
                        <Image src={props.socialMediaPageSelected.platform === "instagram" ? INSTA_ICON : FB_ICON} className="ml-auto" style={{zIndex: 1, marginTop: '-10px'}}/>
                    </div>
                    <p className="m-auto wizard-pages-page-title">{props.socialMediaPageSelected.name}</p> 
                </Row>
            </Row>
            }
            
            {/* Category Name */}
            <Row className="d-flex">   
                <Col lg={2} className="add-new-product-input-container">
                    <input type="text" className="add-new-product-input w-100" placeholder="Type category name" />
                </Col>

                <Col lg={10} />
            </Row>
           
            {/* ADD PRODUCTS */}
            <Row className="d-flex mt-3">   
                <Col lg={3}>
                    <Row className="justify-content-between">
                        <Col lg={5} className="add-new-product-input-container">
                            <input type="text" className="add-new-product-input w-100" />
                        </Col>

                        <Col lg={5}>
                            <Row className="manage-products-add-btn d-flex">
                                {/* <MdAddCircleOutline className="my-auto mr-1" color={iconColor} size={'20'} /> */}
                                <p className="m-auto">Add category</p>
                            </Row>
                        </Col>
                    </Row>
                </Col>

                <Col lg={7} />
            </Row>

            {/* ENTITIES TABLE */}
            <Row className="mt-5 mr-0">
                <Col lg={10} className="p-3" style={{backgroundColor: 'white', borderRadius: '0.3rem'}}>
                    
                    <Row style={{borderBottom: '1px solid #EBEDF0'}}>
                        <Col lg={6} className="d-flex" style={{backgroundColor: 'red'}}>
                            <div className="mr-auto d-flex">
                                <p className="my-auto manage-products-table-title">Category</p>
                            </div>
                        </Col>

                        <Col lg={6} style={{backgroundColor: 'yellow'}}>
                            <div className="d-flex mr-auto">
                                <p className="mr-5 my-auto manage-products-table-title">Synonyms</p>
                            </div>
                        </Col>
                    </Row>

                     <Row style={{borderBottom: '1px solid #EBEDF0'}}>
                        <Col lg={6} className="d-flex" style={{backgroundColor: 'red'}}>
                            <div className="mr-auto d-flex">
                                <p className="my-auto manage-product-entity-name">Pizza marguerita</p>
                            </div>
                        </Col>

                        <Col lg={6}>
                            {/* <div className="d-flex mr-auto">
                                <p className="mr-5 my-auto manage-products-table-title">Synonyms</p>
                            </div> */}
                            <Row>
                                <Col lg={9}>
                                    <p className="my-auto manage-product-entity-name">Synonyms Boxes</p>
                                </Col>

                                <Col lg={3} className="d-flex">
                                    <Row className="ml-auto">
                                        <FiEdit className="my-auto" color={'#E5007D'} size={'25'} />
                                        <FaTrashAlt className="my-auto ml-3" color={'#E5007D'} size={'25'} />
                                    </Row>
                                </Col>
                            </Row>
                        </Col>
                    </Row>



                    {/* Products */}
                    <div className="d-flex mx-0 w-100 pt-3 pb-3 pr-2 pl-2" style={{borderBottom: '1px solid #EBEDF0'}}>
                        <div className="mr-auto d-flex">
                            <p className="my-auto manage-product-entity-name">Pizza</p>
                        </div>

                        <div className="d-flex ml-auto" style={{ backgroundColor: 'red' }}>
                            <p className="mr-5 my-auto manage-product-entity-name">58</p>
                            <FiEdit className="ml-5 my-auto" color={'#E5007D'} size={'25'} />
                            <FaTrashAlt className="ml-5 my-auto" color={'#E5007D'} size={'25'} />
                        </div>
                        </div>

                        <div className="d-flex mx-0 w-100 pt-3 pb-3 pr-2 pl-2" style={{borderBottom: '1px solid #EBEDF0'}}>
                        <div className="mr-auto d-flex">
                            <p className="my-auto manage-product-entity-name">Pizza</p>
                        </div>

                        <div className="d-flex ml-auto">
                            <p className="mr-5 my-auto manage-product-entity-name">58</p>
                            <FaTrashAlt className="ml-5 my-auto" color={'#E5007D'} size={'25'} />
                        </div>
                    </div>
                </Col>

                <Col lg={2} />
            </Row>

        </div>
    );
}

const mapStateToProps = (state) => {
    return {
      socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default withRouter(connect(mapStateToProps, {})(AddNewProducts));
