import React, { useEffect, useState } from 'react'
import '../Home.css';
import PageCard from './PageCard';

import { connect } from 'react-redux';
import { selectSocialMediaPage, getFbData, getInstaData } from '../../../redux/actions/socialMediaActions';

import {
    Row,
    Col,
    // Image,
} from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import { useSwipeable, Swipeable } from 'react-swipeable'
import Lottie from 'react-lottie';

import animationData from '../../../assets/json/loading.json';


function Pages(props) {

    const [loadingPages, setLoadingPages] = useState(false);

    const defaultOptions = {
        loop: true,
        autoplay: true, 
        animationData: animationData,
        rendererSettings: {
          preserveAspectRatio: 'xMidYMid slice'
        }
    };


    useEffect(() => {
        // Getting insta & fb DATA :
        props.getFbData();
        props.getInstaData();
        // setTimeout(() => {
        //     setLoadingPages(false);
        // },2000);
    },[]);

    useEffect(() => {
        if(props.fbData) {
            console.log("Pages fb data =>", props.fbData);
        }
    },[props.fbData]);
    
    useEffect(() => {
        if(props.instaData) {
            console.log("Pages insta Data =>", props.instaData);
        }
    },[props.instaData]);

    return (
    <>
        <Row>
            {
            props.title 
            ?
                <p className="home-big-title" style={props.titleStyling ? props.titleStyling : {} }>{props.title}</p>
            :
                <p className="home-big-title">Pages Connected</p>
            }
        </Row>

        <Row>
            {
            !loadingPages
            ?
                (
                    <>
                    {/* <OwlCarousel margin={10} items={4}> */}
                    
                    <Col lg={12}>
                    <Swipeable onSwiped={(eventData) => eventHandler} {...config} >
                        You can swipe here!
                        <div  style={{background: 'red'}}>
                            Slide 1
                        </div>
                        <div style={{background: 'blue'}}>
                            Slide 1
                        </div>
                        <div style={{background: 'yellow'}}>
                            Slide 1
                        </div>
                    </Swipeable>
                    </Col>

                    <Col lg={12} className="d-flex">


                        {/* Render Fb Data */}
                        {/* {props.fbData !== null && 
                            Object.entries(props.fbData).map( ([key, tab]) => 
                                tab.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"fb",key)}>
                                    <PageCard platform={"fb"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={null} accessToken={page.access_token} status={key}  team={page.user} admins={page.admins} setLoadingPages={setLoadingPages} />
                                </div>
                        ))} */}
                       

                        {/* Render Insta data */}
                        {/* {props.instaData !== null && 
                            Object.entries(props.instaData).map( ([key, tab]) => 
                                tab.map((page,index) => 
                                <div key={index} onClick={() => props.selectSocialMediaPage(page,"insta",key)}>
                                    <PageCard platform={"insta"} isConnectedPage={page.isConnected} namePage={page.name} idPage={page.idPage} picture={page.imageUrl} accessToken={page.access_token} status={key} team={page.user} admins={page.admins} setLoadingPages={setLoadingPages}  />
                                </div>
                        ))} */}
                    </Col>

                    {/* </OwlCarousel> */}
                    </>
                )
            :
                (
                <div className="d-flex" style={{ width: '100%' }}>
                    <div className="m-auto">
                        <Lottie options={defaultOptions} width={200} />
                    </div>
                </div>
                )
            }
        </Row>
    </>
    );
}

const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { selectSocialMediaPage,getFbData, getInstaData })(Pages);
