import React, { useState, useEffect } from 'react'
import './Login.css';
import Facebook from './Facebook';
import axios from 'axios';
import {host} from '../../config';

// Components :
import Navbar from '../Components/Navbar';

import {
    Container,
    Row,
    Col,
    Image,
    Modal,
    Form
    // Nav,
} from 'react-bootstrap';

import {
    Link,
    Redirect,
} from 'react-router-dom';

import Swal from 'sweetalert2/dist/sweetalert2.js';


// Icons & Images:
import { IoIosClose } from 'react-icons/io';
const LANDING_IMG = require('../../assets/images/login/landing-img.png');
const MIND_ICON = require('../../assets/images/login/mind.png');
const CHATBOT_ICON = require('../../assets/images/login/chatbot.png');
// const MIND_ICON_W = require('../../assets/images/login/mind_w.png');
// const CHATBOT_ICON_W = require('../../assets/images/login/chatbot_w.png');
const CORE_AI_ICON = require('../../assets/images/login/coreai.svg');
// const CORE_AI_ICON_W = require('../../assets/images/login/coreai_w.svg');


function LandingPage() {
    // To hover on the card:
    // const [hoverCard,setHoverCard] = useState(false);
    // const [hoverCard1,setHoverCard1] = useState(false);
    // const [hoverCard2,setHoverCard2] = useState(false);

    // Popup Config :
    const popup = Swal.mixin({
        customClass: {
          confirmButton: 'wizard-pages-active-btn py-2 px-3',
        },
        buttonsStyling: false
    });

    const [showContact, setShowContact] = useState(false);
    const [contactName, setContactName] = useState("");
    const [contactLastName, setContactLastName] = useState("");
    const [contactOrg, setContactOrg] = useState("");
    const [contactEmail, setContactEmail] = useState("");
    const [contactPhone, setContactPhone] = useState("");
    const [contactSubject, setContactSubject] = useState("");
    const [contactMsg, setContactMsg] = useState("");


    const auth_token = localStorage.getItem('authtoken');
    const userData = JSON.parse(localStorage.getItem('userData'));

    const allMenuItems = [
        { title: 'Contact us' , route: 'contact-link' },
        { title: 'What is modo' , route: 'what-is-modo' },
        { title: 'How to use it' , route: 'how-to-use-it' },
    ];

    const isValidEmail = (email) => {
        const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(String(email).toLowerCase());
    }

    const sendContactForm = () => {

        if(contactName === "") {
            popup.fire({
                icon: 'error',
                title: 'You have to type a valid NAME please !',
                confirmButtonText: 'Okay',
            });
        } else if( contactLastName === "" ) {
            popup.fire({
                icon: 'error',
                title: 'You have to type a valid LAST NAME please !',
                confirmButtonText: 'Okay',
            });
        } else if (contactOrg === "") {
            popup.fire({
                icon: 'error',
                title: 'You have to type a valid ORGANIZATION please !',
                confirmButtonText: 'Okay',
            });
        } else if (!isValidEmail(contactEmail)) {
            popup.fire({
                icon: 'error',
                title: 'You have to type a valid EMAIL please !',
                confirmButtonText: 'Okay',
            });
        } else if (contactPhone === "") {
            popup.fire({
                icon: 'error',
                title: 'You have to type a valid PHONE NUMBER please !',
                confirmButtonText: 'Okay',
            });
        } else if (contactSubject === "") {
            popup.fire({
                icon: 'error',
                title: 'You have to type a valid SUBJECT please !',
                confirmButtonText: 'Okay',
            });
        } else if (contactMsg === "") {
            popup.fire({
                icon: 'error',
                title: 'You have to type a valid MESSAGE please !',
                confirmButtonText: 'Okay',
            });
        } else {
            // SEND THE CONTACT FORM

            let objectToSend = {
                name: contactName,
                last_name: contactLastName,
                org: contactOrg,
                email: contactEmail,
                phone: contactPhone,
                subject: contactSubject,
                msg: contactMsg,
                status: false,
            };

            // console.log("im sending to nour", objectToSend);
            
            axios.post(host + `/api/v1/contact/addContact`,objectToSend)
            .then(res => {
                console.log('/api/v1/contact/addContact APi Response =>', res.data.data);
                popup.fire({
                    icon: 'success',
                    title: 'Sent succesfully !',
                    confirmButtonText: 'Thanks',
                });
                setShowContact(false) ;
            })
            .catch((err) => {
                console.log("/api/v1/contact/addContact APi post Api error  =>", err)
                popup.fire({
                    icon: 'error',
                    title: 'Connexion error please try again !',
                    confirmButtonText: 'Okay',
                });
            })
        }
    }

    // Rendering Methods
    const renderContactModal = () => (
        <Modal show={showContact} size={"lg"} centered>
            <Modal.Body style={{background: '#F5F5F5', borderRadius: '10px'}}>
                <Col lg={12}>
                    <Row>

                        <Col lg={12} className="d-flex mb-1">
                            <IoIosClose color={"#B4B4B4"} size={30} className="ml-auto" style={{cursor: 'pointer'}} onClick={() => { setShowContact(false) }} />
                        </Col>

                        <Col lg={12} className="d-flex mb-2">
                            <p className="m-auto contact-us-title">Contact us</p>
                        </Col> 

                        <Col lg={12}>
                            <Form>
                                <Form.Row className="mb-3">
                                    <Col lg={6}>
                                        <Form.Label  id="contact-form-label">First name *</Form.Label>
                                        <Form.Control onChange={(e) => setContactName(e.target.value)}id="contact-form-input" />
                                    </Col>

                                    <Col lg={6}>
                                        <Form.Label id="contact-form-label">Last name *</Form.Label>
                                        <Form.Control onChange={(e) => setContactLastName(e.target.value)}id="contact-form-input" />
                                    </Col>
                                </Form.Row>
                                
                                <Form.Row className="mb-3">
                                    <Col lg={6}>
                                        <Form.Label id="contact-form-label">Organization *</Form.Label>
                                        <Form.Control onChange={(e) => setContactOrg(e.target.value)}id="contact-form-input" />
                                    </Col>

                                    <Col lg={6}>
                                        <Form.Label id="contact-form-label">Email *</Form.Label>
                                        <Form.Control onChange={(e) => setContactEmail(e.target.value)}id="contact-form-input" />
                                    </Col>
                                </Form.Row>

                                <Form.Row className="mb-3">
                                    <Col lg={6}>
                                        <Form.Label  id="contact-form-label">Phone</Form.Label>
                                        <Form.Control type="number" onChange={(e) => setContactPhone(e.target.value)}id="contact-form-input" />
                                    </Col>

                                    <Col lg={6}>
                                        <Form.Label id="contact-form-label">Subject</Form.Label>
                                        <Form.Control onChange={(e) => setContactSubject(e.target.value)}id="contact-form-input" />
                                    </Col>
                                </Form.Row>

                                <Form.Row className="mb-3">
                                    <Col lg={12}>
                                        <Form.Label  id="contact-form-label">Message</Form.Label>
                                        <Form.Control onChange={(e) => setContactMsg(e.target.value)}as="textarea" rows={3} id="contact-form-input" />
                                    </Col>
                                </Form.Row>
                                
                                <Form.Row className="justify-content-center mb-2">
                                    <div className="contact-form-button py-2 text-center" onClick={() => sendContactForm()}>
                                        Send
                                    </div>
                                </Form.Row>


                            </Form>   
                        </Col>

                    </Row>
                       
                </Col>
            </Modal.Body>
        </Modal>
    );

    return (
        <>
        { auth_token !== null && userData !== null && <Redirect to="/home/dashboard" /> }
        <Navbar menuItems={allMenuItems} loginBtn={true} setShowContact={setShowContact} />

        <Container fluid>
        {showContact && renderContactModal()}
            {/* BLOC 1 */}
            <Row className="mb-5 justify-content-center" style={{background: "#F9F9F9" , padding: '63px 0px',marginTop: '100px'}}>
                <Col lg={4} className={"d-flex"} style={{backgroundColor: ''}} >
                    {/* <div className="m-auto d-flex justify-content-between flex-column"> */}
                    <div className={"mx-auto mt-auto mb-5"}>
                        <Row className={""}>
                            <Col lg={12} className="d-flex"> <p className="landing-big-text ">Conversation is <br/> a first step of a business </p></Col>
                            <Col lg={12} className="d-flex"><p className="landing-desc mb-5 mt-4">Streamline your Moderation <br/> team efforts</p></Col>
                            <Col lg={12} className="d-flex mt-3"><Facebook type="landing" content="START MY FREE TRIAL"/></Col>
                            <Col lg={12} className="d-flex mt-3"><p className="no-credit-required">** No credit card required</p></Col>
                        </Row>
                    </div>
                    {/* </div> */}
                </Col>

                <Col lg={6} className={"d-flex"} style={{backgroundColor: ''}}>
                    <Row>
                        <Col lg={12}>
                            <Image className={"mx-auto mt-2"} src={LANDING_IMG} style={{ maxWidth: '650px'}}/>
                        </Col>

                        <Col lg={12} className="d-flex">
                            <p className="m-auto landing-page-slogan">Schedule your Agents, Define your replies and Engage your followers </p>
                        </Col>
                    </Row>
                </Col>
            </Row>


            {/* CARDS */}
            {/* <Row className="d-flex justify-content-around m-5"  style={{paddingTop: '100px'}}> */}
            <a id="what-is-modo"></a>
            <Row className="justify-content-center"  style={{paddingTop: '100px',paddingBottom: '100px'}}>
                <Col xs={10} md={10} lg={10} xl={10} className="">
                    <Row className="justify-content-center" >

                        <Col xs={12} md={3} lg={4} xl={4} className="d-flex p-0">
                            <div className="my-auto mr-auto landing-card">
                                <Row className="d-flex mb-3">
                                    <Image src={MIND_ICON} fluid className="mr-auto landing-card-icon" />          
                                </Row>

                                <Row>
                                    <p className="landing-card-title">Own machine learning</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc-blue">Based on our Collaborative NLU building Engine Nuha.ai</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc">More then 15 Machine Learned recurent Questions and 5,000 built-in products. Its also possible to define specific keywords.<div style={{color: '#333335',display:'inline'}} >  Supported language: French, English, Arabic, Tunisian dialect</div></p>
                                </Row>

                                <Row>
                                    <Link to="#" className="landing-card-btn">Learn more</Link>
                                </Row>
                            </div>
                        </Col>

                        <Col xs={12} md={3} lg={4} xl={4} className="d-flex p-0">
                            <div className="m-auto landing-card">
                                <Row className="d-flex mb-3">
                                    <Image src={CHATBOT_ICON} fluid className="mr-auto landing-card-icon"/>
                                </Row>

                                <Row>
                                    <p className="landing-card-title">For small business owners</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc-blue">Do you receive a lot of  comments asking for price or products details?</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc">Use Modo to auto-reply Facebook or instagram comments and <div style={{color: '#333335',display:'inline'}} >process up to 80% of recurrent Questions..</div> define specific replies, and convert followers with call-to-action buttons!</p>
                                </Row>

                                <Row>
                                    <Link to="#" className="landing-card-btn">Learn more</Link>
                                </Row>
                            </div>
                        </Col>

                        <Col xs={12} md={3} lg={4} xl={4} className="d-flex p-0">
                            <div className="my-auto ml-auto landing-card">
                                <Row className="d-flex mb-3">
                                    <Image src={CORE_AI_ICON} fluid className="mr-auto landing-card-icon"/>
                                </Row>

                                <Row>
                                    <p className="landing-card-title">For Agencies</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc-blue">Are you busy managing your clients Facebook and Instagram pages?</p>
                                </Row>

                                <Row>
                                    <p className="landing-card-desc">Stay engaged with your clients audience when running Facebook or Instagram Ads <div style={{color: '#333335',display:'inline'}} > without replying comments one by one.</div> Run your <div style={{color: '#333335',display:'inline'}} > Quiz </div> and congratulate all your winners!</p>
                                </Row>

                                <Row>
                                    <Link to="#" className="landing-card-btn">Learn more</Link>
                                </Row>
                            </div>
                        </Col>
                        
                    </Row>
                </Col>
            </Row>
    
        </Container>
        </>
    )
}

export default LandingPage;
