import React, { useState, useRef } from 'react';

import {
    Row,
    Col,
    Popover,
    Overlay,
    Modal,
    Image,
    Form,
} from 'react-bootstrap';
import axios from 'axios';
import ReactTooltip from 'react-tooltip';
import Keyboard from 'react-simple-keyboard';
import layout from "simple-keyboard-layouts/build/layouts/arabic";
import Picker from 'emoji-picker-react';


import { connect } from 'react-redux';
import { addGenericResponse, updateGenericResponse } from '../../../redux/actions/wizardActions';
import { getAllProductCategories } from '../../../redux/actions/socialMediaActions';
import { host } from '../../../config';

import { FaTrashAlt } from 'react-icons/fa';
import { IoIosArrowDown,IoIosClose } from 'react-icons/io';
import { AiOutlineCloseCircle } from 'react-icons/ai';
import { MdAddCircleOutline } from 'react-icons/md';
const LETTER_ICON = require('../../../assets/images/home/3a.png');
const EMO_ICON = require('../../../assets/images/home/emo.png');
const BALISE_ICON = require('../../../assets/images/home/balise.png');


function WizardIntentDetails(props) {
    /*!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!! CHAAAANNGEEE THE STATE TO "save" !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!*/
    const [intentState, setIntentState] = useState('save');
    /*!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!! CHAAAANNGEEE THE intentState TO "save" !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!*/
    const [products, setProducts] = useState([]);

    const [genericResponse, setGenericResponse] = useState('');
    const [genericButtons, setGenericButtons] = useState('');
    const [specificResponse, SetSpecificResponse] = useState('');
    const [specificButtons, SetSpecificButtons] = useState('');

    const [productName, setProductName] = useState('');
    
    const [newCategory, setNewCategory] = useState('');
    const [categorySelected, setCategorySelected] = useState('');
    const [allCategories, setAllCategories] = useState([]);

    const [savedProductToSend, setSavedProductToSend] = useState('');
    
    const [show, setShow] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [showModalInput, setShowModalInput] = useState(false);
    const [showKeyBoard, setShowKeyBoard] = useState(false);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const [chosenEmoji, setChosenEmoji] = useState(null);
    const [target, setTarget] = useState(null);
    const ref = useRef(null);

    const handleModal = () => {
        setShow(false);
        setShowModal(!showModal);
    };

    const handleClick = (event) => {
        setShow(!show);
        setTarget(event.target);
    };

    const handleSaveGenericResponse = (intent,wizardIdProject) => {
        let project = {
            genericAnswer : genericResponse,
            intent: intent,
        };
        
        if(intentState === 'save') {
            props.addGenericResponse(project,wizardIdProject);
            setIntentState('update');
        } else {
            props.updateGenericResponse(project,wizardIdProject);
        }
    };
    
    const addNewProduct = (idProjet) => {
        
        // First update api : entity === categorie :
        let productToSend = {
            idPage: props.wizardSelectedPage.id,
            typePage : props.wizardSelectedPage.platform,
            entity: productName,
            intent: props.intent,
            genericAnswer: genericResponse,
            specificAnswer: specificResponse,
            categorie: productName,
        };

        axios.put(`${host}/api/v1/secure/project/firstupdate/${idProjet}`, productToSend, {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
        .then( res => {
            // console.log('first update response redux =>', res.data)

            if (res.data.data === "not exist") {
                setShow(!show);
                setSavedProductToSend(productToSend);

                // Call the categories api :
                axios.get(`${host}/api/v1/secure/project/get/entitiesproject/${props.wizardSelectedPage.platform}/${props.wizardSelectedPage.id}`)
                    .then((res) => console.log("Categoriiiiiiiiiiiiiiiiiiiies", res.data))
                    .catch((err) => console.log("addNewProduct WizardIntentDetails =>", err))

                // setShowModal(true);
            } else {
                setProducts(products => [...products, productToSend ]);
                // Reset
                setShow(!show);
                setProductName('');
                SetSpecificResponse('');
            };
        })
        .catch( err => console.log("Wizard Intent Detail addNewProduct err", err))
    };

    const addNewProductViaModal = (idProjet) => {
        // Send the update with the new category name :) :
        let productToSend = savedProductToSend;
        productToSend.categorie = categorySelected;
        
        return axios.put(host + `/api/v1/secure/project/update/${idProjet}`,productToSend, {
            headers: {
                'authorization': 'Bearer ' + localStorage.getItem('authtoken')
            }
        })
        .then(res => {
            console.log('after Updatteeeeee :D =>', res.data);
       
            setProducts(products => [...products, savedProductToSend ]);
            // // Reset
            setProductName('');
            SetSpecificResponse('');
            setShowModal(false);
        })
        .catch((err) => console.log("addNewProductViaModal => ", err))
    };

    const deleteProduct = (index) => {
        let oldProducts = [...products];

        oldProducts.splice(index,1);
        setProducts(oldProducts);    
    };

    const pushNewCategory = () => {       
        setAllCategories(categories => [...categories, newCategory]);
        setShowModalInput(false); 
    };

    const handleCategorySelection = (e) => {
        setCategorySelected(e.target.value);
    };

    const handleArabicKeyboardChange = (input) => {
        let newSpecifiResponse = `${specificResponse}${input.charAt(input.length - 1)}`;
        SetSpecificResponse(newSpecifiResponse);
    };
    
    
    const onKeyPress = (button) => {
        if (button.toString() == "{enter}") {
            setShowKeyBoard(false)
        }
    };
    
    const onEmojiClick = (event, emojiObject) => {
        let newSpecifiResponse = `${specificResponse}${emojiObject.emoji}`;
        SetSpecificResponse(newSpecifiResponse);
        // setChosenEmoji(emojiObject);
    };

    return (
        <Col lg={5} className="mb-3 mr-3" key={props.intent.id}>
            
            <div className="d-flex flex-column wizard-config-intents-container" style={{borderRadius: '0.5rem'}}>
                
                <div className="justify-content-between d-flex flex-row mx-0 p-0  wizard-config-intents-top-row">
                    <p className="my-auto wizard-config-intent-title p-2">{props.intent}</p>

                    <div className="my-auto d-flex p-2">
                        <FaTrashAlt  color={"#818E94"} size={"20"} className="m-auto" style={{cursor: 'pointer'}} onClick={ () => props.stateManagment(props.selectedIntents.filter( intent =>(intent !== props.intent ))) } />
                        <IoIosArrowDown color={"#818E94"} size={"20"} className="my-auto ml-2" style={{cursor: 'pointer'}} />
                    </div>
                </div>
                
              
                <div>
                    <div className="mr-2 ml-2 mt-2 mb-2">
                        <label className="wizard-config-textarea-label">Generic Response</label>
                        <textarea onChange={(e) => setGenericResponse(e.target.value)} value={genericResponse} className="wizard-config-textarea" rows="5"/>
                    </div>

                    <div className="d-flex">
                        {
                            intentState === 'save' 
                            ?
                                (<div className="ml-auto mr-2 mb-2 wizard-config-update-btn" onClick={() => handleSaveGenericResponse(props.intent,props.wizardIdProject)}>Save</div>)
                            :
                                (<div className="ml-auto mr-2 mb-2 wizard-config-update-btn" onClick={() => handleSaveGenericResponse(props.intent,props.wizardIdProject)}>Update</div>)
                        }
                    </div>

                    {/* PRODUCTS */}
                    {
                        intentState === 'update' &&
                        <Col lg={11} className="mx-auto mt-4">
                            <Row className="justify-content-between">
                                {
                                    products.length > 0 &&
                                    products.map((product,index) => 
                                        <Col key={index} lg={5} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-product-box">
                                            {
                                                product.entity.length > 8
                                                ?
                                                <p data-for='entityTip' data-tip={product.entity} className="m-auto wizard-config-product-name">{product.entity.substring(0,7)}...</p>
                                                :
                                                <p className="m-auto wizard-config-product-name">{product.entity}</p>
                                            }
                                            <AiOutlineCloseCircle className="my-auto ml-1" color={"#F49BCC"} size={"20"} style={{cursor: 'pointer'}} onClick={() => deleteProduct(index)} />
                                            <ReactTooltip id='entityTip' textColor='#fff' backgroundColor='#E5007D' />
                                        </Col> 
                                    )
                                }    
                                
                                <Col lg={5} onClick={handleClick} className="d-flex flex-row mb-3 pt-2 pb-2 wizard-config-add-product-box">
                                    <MdAddCircleOutline className="my-auto mr-2" color={"#4080FC"} size={"20"} style={{cursor: 'pointer'}} />
                                    <p className="my-auto wizard-config-add-product-name">Add product</p>
                                </Col>
                            </Row>
                        </Col>
                    }
                </div>
            </div>

            <Modal show={showModal} onHide={handleModal} centered>
                <Modal.Body>          
                    <Row className="d-flex mb-3">
                        <IoIosClose className="ml-auto mr-3" color={'#9F9F9F'} size={'28'} onClick={handleModal} style={{cursor: 'pointer'}} />
                    </Row>

                    <Row className="d-flex mb-3">
                        <p className="m-auto wizard-config-intent-title">Please specify the category</p>
                    </Row>

                    <Row className="mb-3">
                        <Col lg={1} />

                        <Col lg={10} className="d-flex justify-content-between p-0 mx-0">
                            <Col lg={8} className="d-flex">
                                {showModalInput && 
                                    <input 
                                        type="text" 
                                        className="mr-auto w-100 my-auto add-new-category-input-container" 
                                        onChange={(e) => setNewCategory(e.target.value)}
                                        value={newCategory}
                                        onKeyDown={(e) => e.key === 'Enter' && pushNewCategory()}
                                    />
                                }
                            </Col>

                            <Col lg={4} className="d-flex">
                                <p 
                                    className="ml-auto my-auto wizard-config-intent-name" 
                                    style={{cursor: 'pointer'}} 
                                    onClick={() => setShowModalInput(true)}
                                >
                                create new
                                </p>
                            </Col>
                        </Col>

                        <Col lg={1} />
                    </Row>

                    <Row>
                        <Col lg={1} />

                        <Col lg={10}>
                            <Form.Group className="d-flex w-100">
                                <Form.Control 
                                    className="wizard-config-intent-title" 
                                    as="select" 
                                    disabled={showModalInput || allCategories.length === 0} 
                                    onChange={handleCategorySelection}
                                    value={categorySelected} 
                                    custom
                                >
                                    <option className="wizard-config-intent-title">Choose Category...</option>
                                    {allCategories.length > 0 
                                    && 
                                    allCategories.map((category,index) => 
                                        <option key={index} className="wizard-config-intent-title" value={category}>{category}</option>
                                    )}
                                </Form.Control>
                            </Form.Group>
                        </Col>

                        <Col lg={1} /> 
                    </Row>
                    
                    <Row className="mb-3">
                        <div className="m-auto mb-2 wizard-config-update-btn" onClick={() => addNewProductViaModal(props.wizardIdProject)}>Add</div>
                    </Row>
                </Modal.Body>
            </Modal>
            

            <div ref={ref}>
                <Overlay
                    show={show}
                    target={target}
                    placement="right"
                    container={ref.current}
                    containerPadding={50}
                >
                    <Popover id="popover-contained"> 
                        <Popover.Content>
                            <div className="d-flex">
                                <IoIosClose className="ml-auto" color={'#9F9F9F'} size={'25'} onClick={handleClick} style={{cursor: 'pointer'}} />
                            </div>
                            <div className="mr-2 ml-2 mt-2 mb-2">
                                <label className="wizard-config-textarea-label">Product name</label>
                                <input onChange={(e) => setProductName(e.target.value)} value={productName} className="wizard-config-textarea" />
                            </div>
                            
                            <div className="mr-2 ml-2 mt-2 mb-2">
                                <label className="wizard-config-textarea-label">Specific response</label>

                                <Row className="d-flex flex-column">
                                    <Col lg={12} className="d-flex mb-2 px-0 pl-2">
                                        <Row className="d-flex ml-2">
                                            <div className="d-flex mr-2">
                                                <Image src={BALISE_ICON} className="m-auto my-agents-msgs-tiny-icon" style={{ width: '21px'}} />    
                                            </div>                                

                                            <div className="d-flex mr-2">
                                                <Image src={EMO_ICON} onClick={() => setShowEmojiPicker(!showEmojiPicker)} className="m-auto my-agents-msgs-tiny-icon" style={{ width: '19px'}} />
                                                
                                            </div>

                                            <div className="my-agents-msgs-tiny-box d-flex mr-2" style={{ height: '29px', width: '29px' }}>
                                                <Image src={LETTER_ICON} onClick={ () => setShowKeyBoard(!showKeyBoard)} className="m-auto my-agents-msgs-tiny-icon" style={{ width: '11px'}} />
                                                {showKeyBoard &&
                                                    <div className="wizard-config-keyboard-container">
                                                        <Keyboard onChange={handleArabicKeyboardChange} onKeyPress={onKeyPress} layout={layout} />
                                                    </div>
                                                }
                                                {showEmojiPicker &&
                                                    <div className="wizard-config-emoji-container" >
                                                        <Picker onEmojiClick={onEmojiClick} />
                                                    </div>
                                                }
                                            </div>
                                        </Row>
                                    </Col>
                                        
                                    <Col lg={12} className="mb-2">
                                        <textarea onChange={(e) => SetSpecificResponse(e.target.value)} value={specificResponse} className="wizard-config-textarea" rows="3"/>
                                        <hr style={{borderColor: '#B4B4B4', borderWidth: '1.5px'}}/>
                                    </Col>

                                    <Col lg={12} className="d-flex flex-row">
                                        {/* ADD BUTTON */}
                                        <Col lg={4}>
                                            <Row className="wizard-config-quick-replies-btn justify-content-around">
                                                <p className="">Add button</p>
                                                <AiOutlineCloseCircle className="" color={"#1C1C1B"} size={"10"} style={{cursor: 'pointer'}} onClick={() => console.log("Delete")} />
                                            </Row>
                                        </Col>

                                    </Col>
                                </Row>

                            </div>

                            <div className="mr-2 ml-2 mt-2 mb-2 d-flex">
                                <div className="ml-auto mb-2 wizard-config-update-btn" onClick={() => addNewProduct(props.wizardIdProject)}>Add</div>
                            </div>
                        </Popover.Content>
                    </Popover>
                </Overlay>
            </div>
        </Col>
    )
}

const mapStateToProps = (state) => ({
    wizardIdProject: state.wizardR.wizardIdProject,
    wizardIntentType: state.wizardR.wizardIntentType,
    wizardSelectedPage: state.wizardR.wizardSelectedPage,
    // wizardStep: state.wizardR.wizardStep,
})

export default connect(mapStateToProps, { addGenericResponse, updateGenericResponse, getAllProductCategories })(WizardIntentDetails)
