const initialState = {
    userdata: {},
    token: "",
    pages: []
}

const auth = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN':
            return { ...state, userdata: { ...action.user.data.data.user }, token: action.user.data.data.token, pages: { ...action.user.data.data.user.pages } }
        default:
            return state
    }
}
export default auth
