import React, {useState} from 'react'

import {
    Row,
    Col,
} from 'react-bootstrap';

import { connect } from 'react-redux';

function WizardSteps(props) {

    const [allSteps, setAllSteps] = useState([
        { title: 'Page Selection', number: '1', circleClass: 'wizard-step-circle-active', numClass: 'wizard-step-num-active', titleClass: 'wizard-step-title-active'},
        { title: 'Post Selection', number: '2', circleClass: 'wizard-step-circle', numClass: 'wizard-step-num', titleClass: 'wizard-step-title'},
        { title: 'Automation config', number: '3', circleClass: 'wizard-step-circle', numClass: 'wizard-step-num', titleClass: 'wizard-step-title'},
        { title: 'Test & validation', number: '4', circleClass: 'wizard-step-circle', numClass: 'wizard-step-num', titleClass: 'wizard-step-title'},
    ]
    )

    return (
    <Row className="d-flex mt-5">
                    
        <Col lg={1} />

        {
            allSteps.map((step,index) => {

                // console.log("Comparaison =>",index, "with ", props.wizardStep, " => ", props.wizardStep === index);
                let comp = props.wizardStep === index;

                return (
                    <Col className="d-flex flex-column">
                        {/* <div className={props.wizardStep == index ? circleClassActive : circleClass}> */}
                        {/* <div className={"d-flex m-auto" + (comp ? 'wizard-step-circle-active' : 'wizard-step-circle')}> */}
                        <div className={`${comp ? 'wizard-step-circle-active' : 'wizard-step-circle'} d-flex m-auto`}>
                            <p className={`${comp ? 'wizard-step-num-active' : 'wizard-step-num'} m-auto`}>{step.number}</p>
                        </div>
                        <div className="mx-auto mt-2">
                            <p className={`${comp ? 'wizard-step-title-active' : 'wizard-step-title'}`}>{step.title}</p>
                        </div>
                    </Col>
                )
            })

        }

        {/* <Col className="d-flex flex-column">
            <div className="wizard-step-circle-active d-flex m-auto">
                <p className="m-auto wizard-step-num-active">1</p>
            </div>
            <div className="mx-auto mt-2">
                <p className="wizard-step-title-active">Page Selection</p>
            </div>
        </Col>

        <Col className="d-flex flex-column">
            <div className="wizard-step-circle d-flex m-auto">
                <p className="m-auto wizard-step-num">2</p>
            </div>
            <div className="mx-auto mt-2">
                <p className="wizard-step-title">Post Selection</p>
            </div>
        </Col>

        <Col className="d-flex flex-column">
            <div className="wizard-step-circle d-flex m-auto">
                <p className="m-auto wizard-step-num">3</p>
            </div>
            <div className="mx-auto mt-2">
                <p className="wizard-step-title">Automation config</p>
            </div>
        </Col>
        <Col className="d-flex flex-column">
            <div className="wizard-step-circle d-flex m-auto">
                <p className="m-auto wizard-step-num">4</p>
            </div>
            <div className="mx-auto mt-2">
                <p className="wizard-step-title">Test & validation</p>
            </div>
        </Col> */}

        <Col lg={1} />
      
    </Row>
    )
}

const mapStateToProps = (state) => {
    return {
        wizardStep: state.wizardR.wizardStep,
    }
};

export default connect(mapStateToProps, {})(WizardSteps);
