import React, { useEffect } from 'react';
import Pages from '../Pages/Pages';


import { connect } from 'react-redux';
// import {  resetSocialMediaSelections, } from '../../../redux/actions/socialMediaActions';

import {
    Col,
    Row,
} from 'react-bootstrap';

function Dashboard(props) {
    
    let {
        socialMediaPageSelected,

        // FUNCTIONS
    } = props;

    return (
        <div style={{ height: window.innerHeight * 2, marginTop: '135px' }}>
            
            <Pages />

            {socialMediaPageSelected 
            &&
            <>
            <Row>
                <p className="home-big-title" style={{ fontSize: '20px', fontFamily: 'Poppins SemiBold' }}>Most requested intents</p>
            </Row>
            
            <Row>
                <Col lg={10} className="p-4" style={{background: 'white'}}>
                    <Row>
                        <Col lg={8} style={{background: 'gold'}}>
                            <Row className="justify-content-between mx-2">
                                <p>Intents</p>
                                <p>Total</p>
                            </Row>

                            {/* LINE */}
                            {Array.from({length : 5}, (_,i) => 
                                <>
                                <Row key={i} className="justify-content-between mx-2">
                                    <span className="intent-dashboard d-flex" style={{background: "#"+((1<<24)*Math.random()|0).toString(16) }}> 
                                        <p className="m-auto py-2 px-3">Greeting</p>
                                    </span>

                                    <span className="d-flex">
                                        <p className="intent-dashboard-total m-auto">5132</p>
                                    </span>
                                </Row>
                                <hr className="intent-dashboard-separator" />
                                </>
                            )}
                        </Col>

                        <Col lg={4} style={{background: 'cyan'}}>
                            CIRCLE
                        </Col>

                    </Row>
                </Col>
            </Row>



            </>
            }

        </div>
    )
}


const mapStateToProps = (state) => {
    return {
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
    }
};

export default connect(mapStateToProps, { })(Dashboard);
