import React, { useState } from 'react'

function AddSynonym(props) {
    const [synonym, setSynonym] = useState('')
    return (
        <form onSubmit={(e) => {
            e.preventDefault();
            let obj = {
                entity: {
                    content: props.content,
                    synonym
                },
                id: props.entityId
            }
            console.log(obj)
            e.currentTarget.reset()
        }} style={{ padding: "50px" }}>
            <label style={{
                fontFamily: "poppins",
                marginBottom: "10px",
                fontSize: "14px"
            }}>Synonym</label><br /><br />
            <input style={{
                padding: "8px",
                border: "2px solid lightgray",
                borderRadius: "5px",
                outline: "none",
                width: "100%"
            }} type="text" onChange={(e) => setSynonym(e.target.value)} required /><br /><br />

            <button type="submit">Submit</button>
        </form>
    )
}

export default AddSynonym
