import React, { useEffect } from 'react'

import Pages from '../Pages/Pages';

import {
    // Col,
    Row,
    Table,
    Image,
} from 'react-bootstrap';

import moment from 'moment';

import {Link, useHistory  } from 'react-router-dom';

import { connect } from 'react-redux';
import {  resetSocialMediaSelections, } from '../../../redux/actions/socialMediaActions';
import { resetَAllWizard, } from '../../../redux/actions/wizardActions';
import {  getProjectByPage, goToWizardConfig, deleteProjectByPage  } from '../../../redux/actions/myAgentsActions';


const PROJECT_PHOTO = require('../../../assets/images/home/project-photo.png');
const EDIT_ICON = require('../../../assets/images/home/edit-icon.png');
const MSG_ICON = require('../../../assets/images/home/message-icon.png');
const TRASH_ICON = require('../../../assets/images/home/trash-icon.png');


function MyAgents(props) {

    let {
        socialMediaPageSelected,
        agentsProjects,
        
        // Functions
        resetَAllWizard,
        resetSocialMediaSelections,
        getProjectByPage,
        goToWizardConfig,
        deleteProjectByPage,
    } = props;

    let history = useHistory();

    const tableTitles = [
        {title : 'Picture'},
        {title : 'Project name'},
        {title : 'Date'},
        {title : 'Status'},
        {title : 'Edit project'},
    ];

    const renderDate = (date) => {
        return moment(date).format('DD/MM/YY');
    };

    const renderTime = (date) => {
        return moment(date).format('h:mm');
    }

    useEffect(() => {
        resetَAllWizard();
        resetSocialMediaSelections();
    },[]);

    useEffect(() => {
        if(socialMediaPageSelected) {
            getProjectByPage(socialMediaPageSelected);
        }
    },[socialMediaPageSelected])

  
    return (
    <div style={{ height: window.innerHeight * 1.2 }}>
        <Row className="mt-5">
          <p className="my-agents-table-label" style={{ fontSize: '30px', fontFamily: 'Poppins SemiBold' }}>Agents management</p>
        </Row>

        <Pages title="Select a page to manage agents" titleStyling={{color: '#4D4F5C', fontSize: '16px', fontFamily: 'Poppins Medium' }} />

        {props.socialMediaPageSelected &&
        <>
            <Row className="mt-5">
                <p className="my-agents-table-label">Liste des projets</p>
            </Row>
            
            <Row className="mb-5">
                <Table responsive="lg" variant="light">
                    <thead>
                    <tr>
                        {
                        tableTitles.map((item,index) => (
                            <>
                            <th key={index} className="my-agent-table-title">{item.title}</th>
                            {
                                index < tableTitles.length - 1
                                &&
                                <>
                                <th></th>
                                <th></th>
                                <th></th>
                                <th></th>
                                <th></th>
                                </>   
                            }
                            </>    
                        ))
                        }
                    </tr>
                    </thead>
                    <tbody>
                    {agentsProjects && 
                    agentsProjects.map((project,indexProject) => 
                        <tr key={project._id+indexProject}>
                            <td className="d-flex">
                                <div className="m-auto">
                                    {/* <Image src={PROJECT_PHOTO} className={"m-auto"} /> */}
                                    <Image src={project.post.picture} className={"m-auto"} />
                                </div>    
                            </td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td className="d-flex">
                                <div className="m-auto" >
                                    <p className="m-auto my-agent-project-name">{project.post.page.name}</p>
                                </div>
                            </td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td>
                            <div className="flex-column">
                                <p className="m-auto my-agent-project-date">{renderDate(project.createDate)}</p>
                                <p  className="m-auto my-agent-project-time">{renderTime(project.createDate)}</p>
                            </div>
                            </td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td className="d-flex">
                                <div className="m-auto">
                                    <div className="my-agent-status-btn">
                                        {project.trained && project.live 
                                        ? "Active" 
                                        : project.trained && !project.live
                                        ? "Not Active"
                                        : "Draft"
                                        }
                                    </div>
                                </div>
                            </td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td className="d-flex">
                                <div className="m-auto">
                                    <Image src={EDIT_ICON} className={"mr-auto my-agents-icon"} onClick={() => goToWizardConfig(project,socialMediaPageSelected,history)} />
                                    <Link to="/home/my-agents-messages"><Image src={MSG_ICON} className={"ml-2 mr-2 my-agents-icon"} /></Link>
                                    <Image src={TRASH_ICON} className={"ml-auto my-agents-icon"} onClick={() => deleteProjectByPage(project)} />
                                </div>
                            </td>
                        </tr>
                    )}
                    </tbody>
                </Table> 
            </Row>
        </>
        }

    </div>
    );
};

const mapStateToProps = (state) => {
    return {
        fbData : state.socialMediaR.fbData,
        instaData : state.socialMediaR.instaData,
        socialMediaPageSelected : state.socialMediaR.socialMediaPageSelected,
        agentsProjects: state.agentsR.agentsProjects,
    }
};

export default connect(mapStateToProps, { resetSocialMediaSelections, getProjectByPage, goToWizardConfig, resetَAllWizard, deleteProjectByPage  })(MyAgents);
