import React, { Component } from 'react'
import TopMenu from '../components/TopMenu'
import { connect } from 'react-redux'
import { getAllPagesAction, getAllEntity, getConnectedPagesProject } from '../actions'
import PageCart from '../components/PageCart'
import { PieChart } from 'react-minimal-pie-chart';

class Home extends Component {
    state = {
        clickedPage: null
    }
    componentDidMount() {
        this.props.getpages()
            .then(() => this.props.getPagePostProject())
            .then(() => this.props.getAllEntity(this.props.fb))
    }
    componentDidUpdate() {
        console.log(this.state.clickedPage)
    }
    handelclick = (idpage) => this.setState({ clickedPage: idpage })
    render() {
        return (<>
            <TopMenu />
            <div style={{ paddingLeft: 30, paddingRight: 30, paddingTop: 30 }}>
                <h2 style={{ fontSize: 30, color: "rgba(45, 47, 57, 1)" }}>All Pages</h2>
                <div className="pagecarts">
                    {this.props.allpages ?
                        [...this.props.allpages].sort((a, b) => {
                            var x = a.name.toLowerCase();
                            var y = b.name.toLowerCase();
                            if (x < y) { return -1; }
                            if (x > y) { return 1; }
                            return 0;
                        }).map(el => <PageCart handelclick={this.handelclick} show={true} key={el._id} name={el.name} connected={el.isConnected} page={el} />) :
                        <h1>loading</h1>
                    }
                </div>

                <h2 style={{ fontSize: 30, color: "rgba(45, 47, 57, 1)" }}>Most requested intents</h2>
                <div style={{ background: "#fff", padding: 20, display: "flex", justifyContent: "space-between", }}>
                    <div style={{ flex: 2 }}>
                        <table>
                            <tr>
                                <th>Intents</th>
                                <th style={{ textAlign: 'right' }}>Total</th>
                            </tr>
                            <tr>
                                <td><span style={{ padding: "10px 20px", background: 'skyblue', borderRadius: 20, color: "#fff" }}>Greeting</span></td>
                                <td style={{ textAlign: 'right' }}><span>1920</span></td>
                            </tr>
                            <tr>
                                <td><span style={{ padding: "10px 20px", background: 'darkblue', borderRadius: 20, color: "#fff" }}>Price</span></td>
                                <td style={{ textAlign: 'right' }}><span>1170</span></td>
                            </tr>
                            <tr>
                                <td><span style={{ padding: "10px 20px", background: 'pink', borderRadius: 20, color: "#fff" }}>Temperature</span></td>
                                <td style={{ textAlign: 'right' }}><span>980</span></td>
                            </tr>
                            <tr>
                                <td><span style={{ padding: "10px 20px", background: 'coral', borderRadius: 20, color: "#fff" }}>Color</span></td>
                                <td style={{ textAlign: 'right' }}><span>780</span></td>
                            </tr>
                            <tr>
                                <td><span style={{ padding: "10px 20px", background: 'green', borderRadius: 20, color: "#fff" }}>Booking</span></td>
                                <td style={{ textAlign: 'right' }}><span>540</span></td>
                            </tr>
                        </table>
                    </div>
                    <div style={{ flex: 1, padding: 30 }}>
                        <PieChart
                            data={[
                                { title: 'One', value: 30, color: 'skyblue' },
                                { title: 'Two', value: 10, color: 'darkblue' },
                                { title: 'Three', value: 20, color: 'pink' },
                                { title: 'four', value: 10, color: 'green' },
                                { title: 'five', value: 30, color: 'coral' },
                            ]}

                        />
                    </div>
                </div>

                <h2 style={{ fontSize: 30, color: "rgba(45, 47, 57, 1)" }}>Modo Post</h2>
                <div style={{ display: "flex", flexWrap: "wrap" }}>
                    {

                        // props.getPost.hasOwnProperty("idPost") &&
                        // <div style={{ position: 'absolute', top: 120, left: 20, background: "#fff", padding: 20, display: "flex", justifyContent: "space-between", alignItems: "center", boxShadow: "0 0 10px lightgray" }}>
                        //     {props.getPost.full_picture && <img src={props.getPost.full_picture} alt="" style={{ marginRight: 10, width: 51 }} />}
                        //     <span style={{ fontSize: 17, color: "#3B5998" }}>{props.getPost.hasOwnProperty("message") ? (props.getPost.message.length < 15 ? props.getPost.message : props.getPost.message.slice(0, 15) + "...") : "Timeline Post"}</span>
                        // </div>
                        props.allposts.filter(el => el.page.idPage == this.state.clickedPage).map(el =>

                            <div style={{ position: 'absolute', top: 120, left: 20, background: "#fff", padding: 20, display: "flex", justifyContent: "space-between", alignItems: "center", boxShadow: "0 0 10px lightgray" }}>
                                {el.full_picture && <img src={el.full_picture} alt="" style={{ marginRight: 10, width: 51 }} />}
                                <span style={{ fontSize: 17, color: "#3B5998" }}>{el.hasOwnProperty("message") ? (el.message.length < 15 ? el.message : el.message.slice(0, 15) + "...") : "Timeline Post"}</span>
                            </div>

                        )
                    }
                </div>

            </div>
        </>
        )
    }

}

const mapStateToProps = (state) => ({
    fb: state.fbdata.tokenDetail.userID,
    allpages: state.allpages,
    allposts: state.allPosts
})

const mapDispatchToProps = (dispatch) => {
    return {
        getPagePostProject: () => dispatch(getConnectedPagesProject()),
        getpages: () => dispatch(getAllPagesAction()),
        getAllEntity: (id) => dispatch(getAllEntity(id)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Home)
