const initialState = {
    pricingInfos: [],
    currency: {id: "", value: ""},
};

const billingReducer = (state = initialState, action) => {

    switch (action.type) {
      
        case 'DETECT_CURRENCY' :
            return {
                ...state,
                currency: action.payload,
            }

        case 'GET_PRICING_INFO' :
            return {
                ...state,
                pricingInfos: action.payload,
            }

        default:
            return state;
    }
}

export default billingReducer;
