import React, { useEffect } from 'react'
import './AutoWizard.css';

import { connect } from 'react-redux';
import { setWizardIntentType, resetَAllWizard } from '../../../redux/actions/wizardActions';

import {
    Row,
    Col
} from 'react-bootstrap';

import {
    withRouter,
} from 'react-router-dom';

function WizardPopup(props) {
    const { history } = props;

    useEffect(() => {
        props.resetَAllWizard();
    }, [])

    return (
        <>
        <Row style={{ background: 'gold', height: window.innerHeight }} className="p-0">
            <Col lg={3} />

            <Col lg={5} className="d-flex" style={{ background: 'gray' }}>
                <div className="mt-5 mx-auto wizard-popup d-flex flex-column">
                    <Row className="my-auto">
                        <Col lg={2} />

                        <Col>
                            <div className="m-auto wizard-popup-btn d-flex" onClick={() => props.setWizardIntentType('default',history)}>
                                <p className="m-auto">Specific post’s comment</p>
                            </div>
                        </Col>

                        <Col lg={2} />
                    </Row>
                    
                    <Row className="my-auto">
                        <Col lg={2} />

                        <Col>
                            <div className="m-auto wizard-popup-btn d-flex" onClick={() => props.setWizardIntentType('generic',history)}>
                                <p className="m-auto">Generic intents</p>
                            </div>
                        </Col>

                        <Col lg={2} />
                    </Row>
                </div>
            </Col>

            
            <Col lg={3} />

            
            {/* <div lg={12} style={{background: 'cyan'}}>


            </div> */}
            {/* <div className="mt-5 mx-auto wizard-popup d-flex flex-column">
                <Row className="my-auto">
                    <Col lg={2} />

                    <Col>
                        <div className="m-auto wizard-popup-btn d-flex" onClick={() => props.setWizardIntentType('default',history)}>
                            <p className="m-auto">Specific post’s comment</p>
                        </div>
                    </Col>

                    <Col lg={2} />
                </Row>
                
                <Row className="my-auto">
                    <Col lg={2} />

                    <Col>
                        <div className="m-auto wizard-popup-btn d-flex" onClick={() => props.setWizardIntentType('generic',history)}>
                            <p className="m-auto">Generic intents</p>
                        </div>
                    </Col>

                    <Col lg={2} />
                </Row>
            </div> */}

        </Row>
        </>
    );
}


const mapStateToProps = (state) => ({
    wizardIntentType: state.wizardR.wizardIntentType,
});

export default withRouter(connect(mapStateToProps, { setWizardIntentType, resetَAllWizard })(WizardPopup));
