            buttonsStyling: false
        });

        if(props.platform === "insta" && !props.isConnectedPage) {
            // Instagram 
            setLoadingWhileConnecting(true);
            props.connectInstagramPage(props.idPage)
            .then(() => {
                setLoadingWhileConnecting(false);
                props.setLoadingPages(true);
                props.getAllPage()
