import React, { useState, useRef} from 'react';
import {
    Row,
    Col,
    Popover,
    Overlay,
    Modal,
    Image,
    Form,
} from 'react-bootstrap';

const LETTER_ICON = require('../../../../assets/images/home/3a.png');
const LETTER_ICON_ACTIVE = require('../../../../assets/images/home/3a_active.png');
const EMO_ICON = require('../../../../assets/images/home/emo.png');
const EMO_ICON_ACTIVE = require('../../../../assets/images/home/emo_active.png');
const BALISE_ICON = require('../../../../assets/images/home/balise.png');
const BALISE_ICON_ACTIVE = require('../../../../assets/images/home/balise_active.png');



function wizardSpecialInput() {

    return (
        // {/* Special Input */}
        <Row>
            <Col lg={12} className="wizard-special-input">
                
                <Row>
                    <Col lg={12}>
                        <p className="wizard-config-textarea-label">Don't Touch Please</p>
                    </Col>
                </Row>

                <Row>
                    <Col lg={12}>
                        <Row className="d-flex mb-1">
                            <div className="d-flex mr-2">
                                <Image src={showGenericTags ? BALISE_ICON_ACTIVE : BALISE_ICON} className="m-auto my-agents-msgs-tiny-icon" style={{ width: '19px', cursor: 'pointer'}} />    
                            </div>                                

                            <div className="d-flex mr-2">
                                <Image src={showEmojiPickerGeneric ? EMO_ICON_ACTIVE : EMO_ICON}  className="m-auto my-agents-msgs-tiny-icon" style={{ width: '18px', cursor: 'pointer'}} />
                            </div>
            
                            <div className="d-flex mr-2">
                                <Image src={showKeyBoardGeneric ? LETTER_ICON_ACTIVE : LETTER_ICON} className="m-auto my-agents-msgs-tiny-icon" style={{ width: '21px', cursor: 'pointer'}} />
                                
                                {/* {showKeyBoardGeneric &&
                                    <div className="wizard-config-keyboard-container">
                                     <Keyboard onChange={(input) => handleArabicKeyboardChange(input,"generic")} onKeyPress={(button) => onKeyPress(button,"generic")} layout={layout} /> 
                                    </div>
                                */} 

                                {/* {showEmojiPickerGeneric && 
                                    <div className="wizard-config-emoji-container" >
                                        <Picker onEmojiClick={(e,obj) => onEmojiClick(e,obj,"generic")} />
                                    </div>
                                } */}
                            </div>
                        </Row>
                    </Col>
                </Row>

                <Row>
                    <Col lg={12}>
                        <textarea className="wizard-config-textarea" rows="5"/>
                    </Col>
                </Row>
            </Col>
        </Row>
    )
}

const mapStateToProps = (state) => ({
    wizardIdProject: state.wizardR.wizardIdProject,
    wizardIntentType: state.wizardR.wizardIntentType,
    wizardSelectedPage: state.wizardR.wizardSelectedPage,
    wizardSelectedPost: state.wizardR.wizardSelectedPost,
    // wizardStep: state.wizardR.wizardStep,
})

export default connect(mapStateToProps, { addGenericResponse, updateGenericResponse })(wizardSpecialInput)
