import { useMediaQuery } from 'react-responsive';

export const host = 'https://node3.mobilesupportbot.com:5075';

export const trainAgentHost = 'https://node2.mobilesupportbot.com:5051';

// For responsive design
export const isDesktop = useMediaQuery({ query: 'min-width: 992' }); 

export const isTablet = useMediaQuery({ query : 'min-width: 768 and max-width: 991' }); 

export const isMobile = useMediaQuery({ query: 'max-width: 767' });
